ns9:component-version-script.script:
#!/bin/bash
# Import global conf . $global_conf
# SETTING ENVIRONMENT VARIABLES export PATH=$PATH:/usr/local/sbin:/usr/local/bin:/usr/sbin:/usr/bin:/sbin:/bin export JAVA_HOME=$java_home
# VARIABLES ASSIGNMENT INSTALL_PATH=$install_path USER_NAME=$user_name NAMENODE=$namenode SELFIP=$selfip SLAVEIPS=$slaveips DFS_REPLICATION=$dfs_replication
# Function To Display Error and Exit function check_error() { if [ ! "$?" = "0" ]; then error_exit "$1"; fi }
function error_exit() { echo "${PROGNAME}: ${1:-"Unknown Error"}" 1>&2 exit 1 }
cd $INSTALL_PATH/hadoop* HADOOP_HOME=`pwd` echo HADOOP_HOME export HADOOP_HOME export PATH=$PATH:$HADOOP_HOME/bin:$JAVA_HOME/bin
# CHANGING OWNERSHIP chmod -R 777 $HADOOP_HOME chown -R $USER_NAME $HADOOP_HOME
# CONFIGURATION START - MODIFYING haddop-env.sh FOR JRE PATH sed -i "8cJAVA_HOME=$java_home" $HADOOP_HOME/conf/hadoop-env.sh check_error "UNABLE TO EDIT hadoop-env.sh";
# CONFIGURATION CHANGES IN XML FILES :> $HADOOP_HOME/conf/core-site.xml :> $HADOOP_HOME/conf/mapred-site.xml :> $HADOOP_HOME/conf/hdfs-site.xml
cat <<ENDcore > $HADOOP_HOME/conf/core-site.xml <?xml version="1.0"?> <?xml-stylesheet type="text/xsl" href="configuration.xsl"?>
<!-- Put site-specific property overrides in this file. -->
<configuration> <property> <name>fs.default.name</name> <value>hdfs://$NAMENODE:54310</value> <description>The name of the default file system. A URI whose scheme and authority determine the FileSystem implementation. The uri's scheme determines the config property (fs.SCHEME.impl) naming the FileSystem implementation class. The uri's authority is used to determine the host, port, etc. for a filesystem.</description> </property> <property> <name>hadoop.tmp.dir</name> <value>$HADOOP_HOME/temp/</value> <final>true</final> </property> </configuration> ENDcore
check_error "UNABLE TO EDIT core-site.xml";
cat <<ENDmapred > $HADOOP_HOME/conf/mapred-site.xml <?xml version="1.0"?> <?xml-stylesheet type="text/xsl" href="configuration.xsl"?>
<!-- Put site-specific property overrides in this file. -->
<configuration> <property> <name>mapred.job.tracker</name> <value>$SELFIP:8021</value> <description>The host and port that the MapReduce job tracker runs at. If "local", then jobs are run in-process as a single map and reduce task. </description> </property> <property> <name>mapred.temp.dir</name> <value>$HADOOP_HOME/mapred/temp/</value> <final>true</final> </property> <property> <name>mapred.task.tracker.http.address</name> <value>${SlAVEIPS[0]}:50060</value> <final>true</final> </property> <property> <name>mapred.system.dir</name> <value>$HADOOP_HOME/mapred/system</value> <final>true</final> </property> <property> <name>mapred.local.dir</name> <value>$HADOOP_HOME/mapred/hadoop1/data/,$HADOOP_HOME/mapred/hadoop2/data</value> <final>true</final> </property> </configuration> ENDmapred
check_error "UNABLE TO EDIT mapred-site.xml";
cat <<ENDhdfs > $HADOOP_HOME/conf/hdfs-site.xml <?xml version="1.0"?> <?xml-stylesheet type="text/xsl" href="configuration.xsl"?>
<!-- Put site-specific property overrides in this file. -->
<configuration> <property> <name>dfs.replication</name> <value>$DFS_REPLICATION</value> <description>Default block replication. The actual number of replications can be specified when the file is created. The default is used if replication is not specified in create time. </description> </property> <property> <name>hadoop.tmp.dir</name> <value>$HADOOP_HOME/temp/</value> <description>A base for other temporary directories.</description> </property> <property> <name>fs.default.name</name> <value>hdfs://$NAMENODE:54310</value> </property> <property> <name>fs.data.dir</name> <value>$HADOOP_HOME/dfs/data</value> </property> <property> <name>mapred.job.tracker</name> <value>$SELFIP:8021</value> </property> </configuration> ENDhdfs
check_error "UNABLE TO EDIT hdfs-site.xml";
#CREATING DIRECTORIES mkdir -p $HADOOP_HOME/{hadoop3/data,hadoop4/data,temp,data,name,mapred/{temp,system,local,hadoop1/data,hadoop2/data}} check_error "UNABLE TO CREATE CUSTOM DIRECTORIES FOR HADOOP";
:> $HADOOP_HOME/conf/masters echo $SELFIP>> $HADOOP_HOME/conf/masters check_error "UNABLE TO ADD JOBTRACKER IP TO MASTERS FILE.";
## MAKING ENTRY TO THE SLAVES FILE ## # DETERMINING THE NUMBER OF NODES IN CLUSTER IP_ARRAY_LENGTH=`echo ${#SLAVEIPS[*]}`
:> $HADOOP_HOME/conf/slaves for (( i=0;i<$IP_ARRAY_LENGTH;i++)); do echo ${SLAVEIPS[${i}]}>> $HADOOP_HOME/conf/slaves check_error "UNABLE TO ADD JOBTRACKER'S SLAVE IP TO SLAVES FILE."; done
echo "JOBTRACKER CONFIGURATION DONE"

supported-os-details.exec-script:
trap "APPDEXITSTATUS=\$?;printenv > $2;exit $APPDEXITSTATUS" EXIT #Start of Json Parser
#result of getValueFromJson is saved in global variable $JSONVALUE JSONVALUE=""
#use 2 arrays to simulate associate array in Bash 3.x declare -a pathArray declare -a valueArray
function _appd_pathMap_add () { local path local value path=$1 value=$2 pathArray=("${pathArray[@]}" "$path") valueArray=("${valueArray[@]}" "$value") }
function _appd_pathMap_init () { unset pathArray unset valueArray declare -a pathArray declare -a valueArray }
function _appd_pathMap_get () { local path=$1 local length=$((${#pathArray[@]}-1)) for i in `eval echo "{0..$length}"`; do if [ "${pathArray[$i]}" == "$path" ]; then echo "${valueArray[$i]}"; return; fi done; _appd_throw_error "Key path $path not found" }
function _appd_pathMap_output () { local length=$((${#pathArray[@]}-1)) for i in `eval echo "{0..$length}"`; do echo "${pathArray[$i]} values ${valueArray[$i]}" done; }
function _appd_throw_error () { echo "$*" >&2 exit 1 }
function _appd_awk_egrep () { local pattern_string=$1
gawk '{ while ($0) { start=match($0, pattern); token=substr($0, start, RLENGTH); print token; $0=substr($0, start+RLENGTH); } }' pattern=$pattern_string }
function _appd_tokenize () { local GREP local ESCAPE local CHAR
if echo "test string" | egrep -ao --color=never "test" &>/dev/null then GREP='egrep -ao --color=never' else GREP='egrep -ao' fi
if echo "test string" | egrep -o "test" &>/dev/null then ESCAPE='(\\[^u[:cntrl:]]|\\u[0-9a-fA-F]{4})' CHAR='[^[:cntrl:]"\\]' else GREP=_appd_awk_egrep ESCAPE='(\\\\[^u[:cntrl:]]|\\u[0-9a-fA-F]{4})' CHAR='[^[:cntrl:]"\\\\]' fi
local STRING="\"$CHAR*($ESCAPE$CHAR*)*\"" local NUMBER='-?(0|[1-9][0-9]*)([.][0-9]*)?([eE][+-]?[0-9]*)?' local KEYWORD='null|false|true' local SPACE='[[:space:]]+' $GREP "$STRING|$NUMBER|$KEYWORD|$SPACE|." | egrep -v "^$SPACE$" }
function _appd_parse_array () { local index=0 local ary='' read -r token case "$token" in ']');; *) while : do _appd_parse_value "$1" "[$index]" index=$((index+1)) ary="$ary""$value" read -r token case "$token" in ']') break ;; ',') ary="$ary," ;; *) _appd_throw_error "EXPECTED , or ] GOT ${token:-EOF}" ;; esac read -r token done esac value=`printf '[%s]' "$ary"` : }
function _appd_parse_object () { local key local obj='' read -r token case "$token" in '}') ;; *) while : do case "$token" in '"'*'"') key=$token ;; *) _appd_throw_error "EXPECTED string GOT ${token:-EOF}" ;; esac read -r token case "$token" in ':') ;; *) _appd_throw_error "EXPECTED : GOT ${token:-EOF}" ;; esac read -r token _appd_parse_value "$1" "${key:1:$((${#key}-2))}" obj="$obj$key:$value" read -r token case "$token" in '}') break ;; ',') obj="$obj," ;; *) _appd_throw_error "EXPECTED , or } GOT ${token:-EOF}" ;; esac read -r token done esac value=`printf '{%s}' "$obj"` : }
function _appd_parse_value () { local jpath [[ "$2" == \[* ]] && jpath="${1:+$1}$2" || jpath="${1:+$1.}$2" case "$token" in '{') _appd_parse_object "$jpath" ;; '[') _appd_parse_array "$jpath" ;; # At this point, the only valid single-character tokens are digits. ''|[!0-9]) _appd_throw_error "EXPECTED value GOT ${token:-EOF}" ;; *) value=$token ;; esac [ "$value" = '' ] && return _appd_pathMap_add "$jpath" "$value" : }
function _appd_parse () { read -r token _appd_parse_value read -r token case "$token" in '') ;; *) _appd_throw_error "EXPECTED EOF GOT $token" ;; esac }
function _appd_convert_to_script_string () { case `echo "$1" | tr A-Z a-z` in "true") echo true ;; "false") echo false ;; "null") echo "" ;; \"*\") echo "${1:1:$((${#1}-2))}" ;; *) echo "$1" ;; esac }
# # This method will parse JSON String, get value of given JSON keypath. # The result will saved in variable $JSONVALUE # The returned value type will be: # String: whenever (1) the value of given JSON keypath is a JSON string (enclosed by double quotation) # or (2) the value of given JSON keypath is a JSON object (enclosed by brace) # Number: whenever the value of given JSON keypath is a number # Keyword: whenever the value of given JSON keypath is true/false/null # Array: whenever the value of given JSON keypath is a JSON array (enclosed by square) # # This method will automatically remove enclosing double quotation when return String or Array of String # # Example: # getValueFromJson '{"key1":"value1", "key2":{"subkey1":"subValue1"}}' 'key2.subkey1' # $JSONVALUE will be "subValue1" # Example: # getValueFromJson '{"key1":["value1","value2"]}' 'key1[1]' # $JSONVALUE will be "value2" # Example: # getValueFromJson '[{"key1":"value1"}, {"key1":"value2"}]' '[1].key1' # $JSONVALUE will be "value2" # Example: # getValueFromJson '[{"key1":"value1"}, {"key1":"value2"}]' '' # $JSONVALUE will be an array with 2 elements. # first element is '{"key1":"value1"}' # second element is '{"key1":"value2"}' # Example: # getValueFromJson '{"key1":"value1"}' '' # $JSONVALUE will be same as input string. # function getValueFromJson () { local JSONSTRING=$1 local JSONPATH=$2 local tokenTmp local stringValue
_appd_pathMap_init tokenTmp=`mktemp` echo "$JSONSTRING" | _appd_tokenize > $tokenTmp _appd_parse < $tokenTmp rm -rf tokenTmp stringValue=`_appd_pathMap_get $JSONPATH` if [[ $stringValue == \[* ]] ; then local length JSONVALUE=() length=$((${#pathArray[@]}-1)) for i in `eval echo "{0..$length}"`; do if [[ "${pathArray[$i]}" =~ "$JSONPATH" ]]; then local subPath=${pathArray[$i]:${#JSONPATH}} if [[ "$subPath" =~ ^\[[0-9]+\]$ ]]; then local elementValue elementValue=`_appd_convert_to_script_string "${valueArray[i]}"` JSONVALUE=("${JSONVALUE[@]}" "$elementValue") fi fi done else JSONVALUE=`_appd_convert_to_script_string "$stringValue"` fi } #End of Json Parser
# Execute action script source $1 APPDRETURNSTATUS=$? # Unset trap trap - EXIT # Save env variables in output properties file printenv > $2 exit $APPDRETURNSTATUS

ns9:component-version-script.script:
#!/bin/bash
# Import global conf . $global_conf
# SETTING ENVIRONMENT VARIABLES export PATH=$PATH:/usr/local/sbin:/usr/local/bin:/usr/sbin:/usr/bin:/sbin:/bin export JAVA_HOME=$java_home
# VARIABLES ASSIGNMENT INSTALL_PATH=$install_path USER_NAME=$user_name SELFIP=$selfip SLAVEIPS=$slaveips
# Function To Display Error and Exit function check_error() { if [ ! "$?" == "0" ]; then error_exit "$1"; fi }
function error_exit() { echo "${PROGNAME}: ${1:-"Unknown Error"}" 1>&2 exit 1 }
cd $INSTALL_PATH/hadoop* HADOOP_HOME=`pwd` export HADOOP_HOME export PATH=$PATH:$HADOOP_HOME/bin:$JAVA_HOME/bin
# START OF JOB TACKER NODE cd $HADOOP_HOME chmod -R 777 $HADOOP_HOME chown -R $USER_NAME $HADOOP_HOME
su $USER_NAME -c "nohup nice -n 0 $HADOOP_HOME/bin/hadoop --config $HADOOP_HOME/conf/ jobtracker > "jobtracker.log" 2>&1 < /dev/null &" check_error "UNABLE TO INVOKE HADOOP.SH TO START JOBTRACKER";
IP_ARRAY_LENGTH=`echo ${#SlAVEIPS[*]}`
for (( i=0;i<$IP_ARRAY_LENGTH;i++)); do echo ${SlAVEIPS[${i}]}>> /etc/hosts check_error "UNABLE TO ADD JOBTRACKER'S SLAVE IP TO /etc/hosts FILE."; done
# Try 120 times to start post-install scripts, which means the script will wait for about 20mins at most # until the installation finishes (each loop will wait for 10 seconds for the installation to finish.) SLEEP_PERIOD=10 LOOP_ITERATION=120 for (( i = 0 ; i < $LOOP_ITERATION ; i++ )) do `grep -q "mapred.JobTracker: Starting RUNNING" $HADOOP_HOME/jobtracker.log` if [ "$?" == "0" ]; then sleep 10 touch jobtracker_check.html wget http://$SELFIP:50030/jobtracker.jsp -q -O jobtracker_check.html #check_error "UNABLE TO GET HTML PAGE OF JOBTRACKER"; if [ "$?" == "0" ]; then echo "UNABLE TO GET HTML PAGE OF JOBTRACKER" break fi grep -q "Cluster Summary" jobtracker_check.html if [ "$?" == "0" ]; then echo "JOBTRACKER SUCCESFULLY RUNNING" break fi else sleep $SLEEP_PERIOD if [ "$i" == "$(($LOOP_ITERATION - 1))" ]; then error_exit "TIMEOUT ERROR IN STARTING JOBTRACKER"; fi fi done

supported-os-details.exec-script:
trap "APPDEXITSTATUS=\$?;printenv > $2;exit $APPDEXITSTATUS" EXIT #Start of Json Parser
