 name='http.ntlm.auth.kind' value='NTLM'/>" >> $RESPONSE_XML echo "<preference name='http.ntlm.auth.enableIntegrated.win32' value='true'/>" >> $RESPONSE_XML echo "<preference name='com.ibm.cic.common.core.preferences.preserveDownloadedArtifacts' value='true'/>" >> $RESPONSE_XML echo "<preference name='com.ibm.cic.common.core.preferences.keepFetchedFiles' value='false'/>" >> $RESPONSE_XML echo "<preference name='PassportAdvantageIsEnabled' value='false'/>" >> $RESPONSE_XML echo "<preference name='com.ibm.cic.common.core.preferences.searchForUpdates' value='false'/>" >> $RESPONSE_XML echo "<preference name='com.ibm.cic.agent.ui.displayInternalVersion' value='false'/>" >> $RESPONSE_XML echo "<preference name='com.ibm.cic.common.sharedUI.showErrorLog' value='true'/>" >> $RESPONSE_XML echo "<preference name='com.ibm.cic.common.sharedUI.showWarningLog' value='true'/>" >> $RESPONSE_XML echo "<preference name='com.ibm.cic.common.sharedUI.showNoteLog' value='true'/>" >> $RESPONSE_XML if [ "x${PROXY_HOST}" != "x" ]; then echo "<preference name='com.ibm.cic.common.core.preferences.http.proxyEnabled' value='true'/>" >> $RESPONSE_XML echo "<preference name='com.ibm.cic.common.core.preferences.http.proxyHost' value='$PROXY_HOST'/>" >> $RESPONSE_XML echo "<preference name='com.ibm.cic.common.core.preferences.http.proxyPort' value='$PROXY_PORT'/>" >> $RESPONSE_XML if [ "x${PROXY_USERNAME}" != "x" ]; then echo "<preference name='com.ibm.cic.common.core.preferences.http.proxyUsername' value='$PROXY_USERNAME'/>" >> $RESPONSE_XML echo "<preference name='com.ibm.cic.common.core.preferences.http.proxyUserPassword' value='$PROXY_PASSWORD'/>" >> $RESPONSE_XML fi fi
echo "</agent-input>" >> $RESPONSE_XML check_error "Errors during generating response xml.";
