        cmds.add("/c");
        cmds.add(cmdString);
        ProcessBuilder pb = new ProcessBuilder(cmds);
        pb.redirectErrorStream();
        try {
            StringBuilder output = new StringBuilder();
            Process proc = pb.start();
            // attach a watchdog to ensure long-running processes are terminated forcibly
            org.apache.commons.exec.ExecuteWatchdog watchdog = new org.apache.commons.exec.ExecuteWatchdog(120 * 1000);
            watchdog.start(proc);
            BufferedReader input = new BufferedReader(new InputStreamReader(proc.getInputStream()));
