# FEDERATING $PROFILE_NAME WITH DMGR echo "FEDERATING $PROFILE_NAME WITH DMGR" cd $PROFILE_PATH/bin ./addNode.sh $dmgr_hostname 50003 -conntype soap -profileName $PROFILE_NAME -username $ADMIN_USERNAME -password $ADMIN_PASSWORD status=$? while [ "$status" != "0" ] do echo "WAITING FOR 20 SECONDS..." sleep 20 echo "AGAIN FEDERATING THE NODE..." ./addNode.sh $dmgr_hostname 50003 -conntype soap -profileName $PROFILE_NAME -username $ADMIN_USERNAME -password $ADMIN_PASSWORD status=$? done

#!/bin/bash

## FUNCTION TO DISPLAY ERROR AND EXIT
function check_error()
{
   if [ ! "$?" = "0" ]; then
      error_exit "$1";
   fi
}

echo "STARTING E-BUSINESS SERVER"
INSTALL_BASE="$install_base"
INSTALL_LOCATION="$INSTALL_BASE/WebSphere/AppServer"
SERVER_NAME="$server_name"

cd $INSTALL_LOCATION/bin
./startServer.sh $SERVER_NAME
check_error "ERRORS DURING STARTING SERVER.";

print ("getting the IP address for the nics...");
ip="";
nwIpMap = new TreeMap();
ne = NetworkInterface.getNetworkInterfaces();
while (ne.hasMoreElements()) {
    NetworkInterface netFace = (NetworkInterface)ne.nextElement();
    if(!netFace.isLoopback()) {
        Enumeration ae = netFace.getInetAddresses();
        while (ae.hasMoreElements()) {
            InetAddress address = (InetAddress) ae.nextElement();
            StringBuilder macAddress = new StringBuilder();
            if(address instanceof Inet4Address) {
                StringBuilder macAddress = new StringBuilder("");
                if(netFace.getHardwareAddress() != null) {
                    for (int i = 0; i < netFace.getHardwareAddress().length; i++) {
                        Object[] arr = new Object[]{netFace.getHardwareAddress()[i], (i < netFace.getHardwareAddress().length - 1) ? ":" : ""};
                        macAddress.append(String.format("%02X%s", arr));
                    }
                } else {
                    //if there is no MAC address, move to the next interface
                    continue;
                }
                if(StringUtils.isBlank(ip)) {
                    ip=address.getHostAddress();
                }
                nwIpMap.put(macAddress.toString().toUpperCase(), address.getHostAddress());
            }
        }
    }
}
totalBlueprintNics = nic_name_array.length;
totalVMNics = nwIpMap.size();
print ("Network interfaces/IPs found on the VM: " + nwIpMap.toString());
if(totalVMNics < totalBlueprintNics) {
    throw new Exception("Insufficient network interfaces, expected " + totalBlueprintNics + " but got " + totalVMNics);
} else if(totalBlueprintNics != totalVMNics) {
    print("WARNING: Total number of NICs specified in blueprint (" + totalBlueprintNics + ") does not match the number of NICs found on VM (" + totalVMNics + ").");
}
for(index=0;index < totalBlueprintNics; index++) {
    eval("mac="+nic_name_array[index]+"_MAC");
    ipAddr = nwIpMap.get(mac.toUpperCase());
    if(StringUtils.isBlank(ipAddr)) {
        throw new Exception("Invalid MAC address in the input properties: " + nic_name_array[index]+"_MAC=" + mac);
    }
    eval(nic_name_array[index]+"_ip"+"=ipAddr");
}
print("evaluated all the ips for the NICs");

#!/bin/bash
#########CUSTOMIZED PARAMETERS######### INSTALL_BASE="$install_base" INSTALL_LOCATION="$INSTALL_BASE/WebSphere/AppServer"
#########PARAMTERS FROM APP DIRECTOR######### PROFILE_NAME="$profile_name" PROFILE_PATH="$INSTALL_BASE/WebSphere/AppServer/profiles/$PROFILE_NAME" NODE_NAME="$node_name" SERVER_NAME="$server_name" ADMIN_USERNAME="$admin_username" ADMIN_PASSWORD="$admin_password"
# FUNCTION TO DISPLAY ERROR AND EXIT function check_error() { if [ ! "$?" = "0" ]; then error_exit "$1"; fi } function error_exit() { echo "${PROGNAME}: ${1:-"Unknown Error"}" 1>&2 exit 1 }
echo $SHELL
#CHANGING THE SHELL FROM DASH TO BASH cd /bin unlink sh ln -s /bin/bash sh echo $SHELL
# CREATING PROFILE if [ "x${profile_path}" != "x" ]; then PROFILE_PATH="$profile_path" fi JAVA_HOME="/usr/lib/jvm/jre-1.6.0" WAS_HOME=$INSTALL_LOCATION
cd $INSTALL_LOCATION/bin echo "CREATING WEBSPHERE PROFILES" ./manageprofiles.sh -create -profileName $PROFILE_NAME -serverName $SERVER_NAME -profilePath $PROFILE_PATH -nodeName $NODE_NAME -portsFile $INSTALL_LOCATION/profileTemplates/default/actions/portsUpdate/portdef.props -profileTemplate $INSTALL_LOCATION/profileTemplates/default -adminUserName $ADMIN_USERNAME -adminPassword $ADMIN_PASSWORD check_error "ERRORS DURING CREATING PROFILE.";

print ("getting the IP address for the nics...");
ip="";
nwIpMap = new TreeMap();
ne = NetworkInterface.getNetworkInterfaces();
while (ne.hasMoreElements()) {
    NetworkInterface netFace = (NetworkInterface)ne.nextElement();
    if(!netFace.isLoopback()) {
        Enumeration ae = netFace.getInetAddresses();
        while (ae.hasMoreElements()) {
            InetAddress address = (InetAddress) ae.nextElement();
            StringBuilder macAddress = new StringBuilder();
            if(address instanceof Inet4Address) {
                StringBuilder macAddress = new StringBuilder("");
                if(netFace.getHardwareAddress() != null) {
                    for (int i = 0; i < netFace.getHardwareAddress().length; i++) {
                        Object[] arr = new Object[]{netFace.getHardwareAddress()[i], (i < netFace.getHardwareAddress().length - 1) ? ":" : ""};
                        macAddress.append(String.format("%02X%s", arr));
                    }
                } else {
                    //if there is no MAC address, move to the next interface
                    continue;
                }
                if(StringUtils.isBlank(ip)) {
                    ip=address.getHostAddress();
                }
                nwIpMap.put(macAddress.toString().toUpperCase(), address.getHostAddress());
            }
        }
    }
}
totalBlueprintNics = nic_name_array.length;
totalVMNics = nwIpMap.size();
print ("Network interfaces/IPs found on the VM: " + nwIpMap.toString());
if(totalVMNics < totalBlueprintNics) {
    throw new Exception("Insufficient network interfaces, expected " + totalBlueprintNics + " but got " + totalVMNics);
} else if(totalBlueprintNics != totalVMNics) {
    print("WARNING: Total number of NICs specified in blueprint (" + totalBlueprintNics + ") does not match the number of NICs found on VM (" + totalVMNics + ").");
}
for(index=0;index < totalBlueprintNics; index++) {
    eval("mac="+nic_name_array[index]+"_MAC");
    ipAddr = nwIpMap.get(mac.toUpperCase());
    if(StringUtils.isBlank(ipAddr)) {
        throw new Exception("Invalid MAC address in the input properties: " + nic_name_array[index]+"_MAC=" + mac);
    }
    eval(nic_name_array[index]+"_ip"+"=ipAddr");
}
print("evaluated all the ips for the NICs");

