function _appd_parse_value () { local jpath [[ "$2" == \[* ]] && jpath="${1:+$1}$2" || jpath="${1:+$1.}$2" case "$token" in '{') _appd_parse_object "$jpath" ;; '[') _appd_parse_array "$jpath" ;; # At this point, the only valid single-character tokens are digits. ''|[!0-9]) _appd_throw_error "EXPECTED value GOT ${token:-EOF}" ;; *) value=$token ;; esac [ "$value" = '' ] && return _appd_pathMap_add "$jpath" "$value" : }
function _appd_parse () { read -r token _appd_parse_value read -r token case "$token" in '') ;; *) _appd_throw_error "EXPECTED EOF GOT $token" ;; esac }
function _appd_convert_to_script_string () { case `echo "$1" | tr A-Z a-z` in "true") echo true ;; "false") echo false ;; "null") echo "" ;; \"*\") echo "${1:1:$((${#1}-2))}" ;; *) echo "$1" ;; esac }
# # This method will parse JSON String, get value of given JSON keypath. # The result will saved in variable $JSONVALUE # The returned value type will be: # String: whenever (1) the value of given JSON keypath is a JSON string (enclosed by double quotation) # or (2) the value of given JSON keypath is a JSON object (enclosed by brace) # Number: whenever the value of given JSON keypath is a number # Keyword: whenever the value of given JSON keypath is true/false/null # Array: whenever the value of given JSON keypath is a JSON array (enclosed by square) # # This method will automatically remove enclosing double quotation when return String or Array of String # # Example: # getValueFromJson '{"key1":"value1", "key2":{"subkey1":"subValue1"}}' 'key2.subkey1' # $JSONVALUE will be "subValue1" # Example: # getValueFromJson '{"key1":["value1","value2"]}' 'key1[1]' # $JSONVALUE will be "value2" # Example: # getValueFromJson '[{"key1":"value1"}, {"key1":"value2"}]' '[1].key1' # $JSONVALUE will be "value2" # Example: # getValueFromJson '[{"key1":"value1"}, {"key1":"value2"}]' '' # $JSONVALUE will be an array with 2 elements. # first element is '{"key1":"value1"}' # second element is '{"key1":"value2"}' # Example: # getValueFromJson '{"key1":"value1"}' '' # $JSONVALUE will be same as input string. # function getValueFromJson () { local JSONSTRING=$1 local JSONPATH=$2 local tokenTmp local stringValue
_appd_pathMap_init tokenTmp=`mktemp` echo "$JSONSTRING" | _appd_tokenize > $tokenTmp _appd_parse < $tokenTmp rm -rf tokenTmp stringValue=`_appd_pathMap_get $JSONPATH` if [[ $stringValue == \[* ]] ; then local length JSONVALUE=() length=$((${#pathArray[@]}-1)) for i in `eval echo "{0..$length}"`; do if [[ "${pathArray[$i]}" =~ "$JSONPATH" ]]; then local subPath=${pathArray[$i]:${#JSONPATH}} if [[ "$subPath" =~ ^\[[0-9]+\]$ ]]; then local elementValue elementValue=`_appd_convert_to_script_string "${valueArray[i]}"` JSONVALUE=("${JSONVALUE[@]}" "$elementValue") fi fi done else JSONVALUE=`_appd_convert_to_script_string "$stringValue"` fi } #End of Json Parser
