 $vmw_appd_obj_prop_type = (($vmw_appd_json_obj.($vmw_appd_obj_prop_name)).GetType()).Name

 # Export only String AppD's properties; Output Array properties is not currently supported.
 if ($vmw_appd_obj_prop_type.Contains("String")) {
      $vmw_appd_obj_prop_name_expanded = '$' + $vmw_appd_obj_prop_name

      # The ExpandString takes care of three cases. 
      # Case-I when the string is set via input properties
      # Case-II when the string is set via input properties and overridden in script (install/configure..)
      # Case-III lastly when it is only set via script (install/configure..) and left empty in input props
      #
      # Also note that powershell as is does not care if this variable was a computed 
      # or a string variable that filter happens at a different layer.
      #
      $expandedUserVariable = $ExecutionContext.InvokeCommand.ExpandString($vmw_appd_obj_prop_name_expanded)

     ($vmw_appd_obj_prop_name + '=' + $expandedUserVariable) | Add-Content $($Args[2]) 
 }
}

ns9:component-version-script.script:
# Action: Start to run AutoSPInstaller to install and Configure SharePoint server # # Required Props: # - sp_install_root # - domain_account # - domain_password ###################################################################
#Region Properties
$sp_install_location="$sp_install_root\SP" $account="$domain_account" $account_password="$domain_password"
$sp_install_bin="$sp_install_location\AutoSPInstaller" $script_file="$sp_install_bin\RemoteStart.ps1" $inputFile="$sp_install_bin\AutoSPInstallerInput.xml" $original_function_file="$sp_install_bin\AutoSPInstallerFunctions.ps1" $help_function_file="$sp_install_bin\AutoSPInstallerFunctions_modified.ps1"
#EndRegion
#Region Helper Functions
## Function: Check Error Function check_error { if ($error.count -gt 0) { error_exit $args[0] } }
## Function Error Exit Function error_exit { echo $args[0] $error exit 1 }
## Function: Delete File Function DeleteFile([string]$filePath) { If (Test-Path "$filePath" -Verbose) { Remove-Item -Force $filePath } }
## Function: Create Directory Function CreateDir([string]$dirName) { If (!(Test-Path "$dirName" -Verbose)) { Write-Output "create download directory $dirName" New-Item -ItemType Directory $dirName } }
## Function: DeleteAndCreateFile Function DeleteAndCreateFile([string]$filePath) { If (Test-Path "$filePath" -Verbose) { Remove-Item -Force $filePath }
$path = split-path $filePath -Parent $fileName = split-path $filePath -Leaf
CreateDir $path
write-Output "create download directory $fileName" New-Item -ItemType File $filePath }
## Function: Nee-ReRun - check if again round of installation is needed. # Return true if there are errors and at least one of them says "you must re-try this action" Function Need-ReRun { [string]$reRun_keyword='you must re-try this action' foreach ($item in $Error) { if ("$item" -match "$reRun_keyword") { return $true } } return $false }
#EndRegion
#Region Set remoting setting to accept PSSession from remote (WSManCredSSP) client
Function Set-RemoteSettings { # Configures the server for WinRM and WSManCredSSP Write-Host "Configuring PowerShell remoting..." $winRM = Get-Service -Name winrm If ($winRM.Status -ne "Running") {Start-Service -Name winrm} try {Set-ExecutionPolicy Bypass -Force} catch {} try {Enable-PSRemoting -Force} catch {} Enable-WSManCredSSP -Role Server -Force # | Out-Null # Increase the local memory limit to 1 GB Set-Item WSMan:\localhost\Shell\MaxMemoryPerShellMB 1024 }
#EndRegion
#Region Set CredSSP client role for the localhost
Function Set-ClientCredSSP { Enable-WSManCredSSP -Role Client -Force -DelegateComputer $env:COMPUTERNAME # | Out-Null # Delay for several seconds to give the WSManCresSSP enough time to finish the configuration Start-Sleep 5 }
#EndRegion
#Region Pre-install Windows Identity Foundation (KB974405)
# This step is required due to a known issue with the PrerequisiteInstaller.exe over a remote session; # Specifically, because Windows Update Standalone Installer (wusa.exe) blows up with error code 5 # With a fully-patched Windows 2008 R2 server though, the rest of the prerequisites seem OK; so this # function only deals with KB974405 (Windows Identity Foundation). # Thanks to Ravikanth Chaganti (@ravikanth) for describing the issue, and working around it # so effectively: http://www.ravichaganti.com/blog/?p=1888
Function Install-WindowsIdentityFoundation ([string]$sp_install_location) { $remoteQueryOS = Get-WmiObject Win32_OperatingSystem $Error.Clear() If (!($remoteQueryOS.Version.Contains("6.2"))) # Only perform the stuff below if we aren't on Windows 2012 { Write-Host -ForegroundColor White " - Checking for KB974405 (Windows Identity Foundation)..." -NoNewline $wifHotfixInstalled = Get-HotFix -Id KB974405 -ErrorAction SilentlyContinue If ($wifHotfixInstalled) { Write-Host -ForegroundColor White "already installed." } Else { Write-Host -ForegroundColor Black -BackgroundColor White "needed." If ($remoteQueryOS.Version.Contains("6.1")) { $wifHotfix = "Windows6.1-KB974405-x64.msu" } ElseIf ($remoteQueryOS.Version.Contains("6.0")) { $wifHotfix = "Windows6.0-KB974405-x64.msu" } Else {Write-Warning " - Could not detect OS of `"$server`", or unsupported OS."}
$hotfix_file="$sp_install_location\SharePoint\PrerequisiteInstallerFiles\$wifHotfix" If (!(Get-Item $hotfix_file -ErrorAction SilentlyContinue)) { Write-Host -ForegroundColor White " - Windows Identity Foundation KB974405 not found in $env:SPbits\PrerequisiteInstallerFiles" Write-Host -ForegroundColor White " - Attempting to download..." $wifURL = "http://download.microsoft.com/download/D/7/2/D72FD747-69B6-40B7-875B-C2B40A6B2BDD/$wifHotfix" Import-Module BitsTransfer | Out-Null Start-BitsTransfer -Source $wifURL -Destination "$sp_install_location\SharePoint\PrerequisiteInstallerFiles\$wifHotfix" -DisplayName "Downloading `'$wifHotfix`' to $env:SPbits\PrerequisiteInstallerFiles" -Priority Foreground -Description "From $wifURL..." -ErrorVariable err if ($err) {Throw " - Could not download from $wifURL!"; Pause "exit"; break} } Write-Host -ForegroundColor White " - Pre-installing Windows Identity Foundation on `"$env:COMPUTERNAME`" ..." -NoNewline Start-Process -FilePath "$PSHOME\powershell.exe" -ArgumentList "`"$hotfix_file`" /quiet /norestart" -Wait -NoNewWindow Write-Host "Done" } $Error.Clear() } }
#EndRegion
#Region Compose help functions for RemoteStart PS code
Function Compose-HelpPSFile ([string]$autoSPInstallerFunctionsFile, [string]$modifiedFunctionsFile) {
$function_file="$autoSPInstallerFunctionsFile" $help_function_file="$modifiedFunctionsFile"
# Replacement contants for picking all the functions that are needed by RemoteStart PS code $function_identifier="StartTracing|Test-ServerConnection|Install-NetFramework|Install-WindowsIdentityFoundation|Start-RemoteInstaller|Function\sWriteLine|Function\sConvertTo-PlainText" $function_end_identifier='^\}'
# Replacement constants for modifying new PSCredential object in the following three functions $password_identifier="Install-NetFramework|Install-WindowsIdentityFoundation|Start-RemoteInstaller" $password_match="New-Object\sSystem.Management\.Automation\.PsCredential" $replacement_password=' If ($password) {$credential = New-Object System.Management.Automation.PsCredential $env:USERDOMAIN\$env:USERNAME,$(ConvertTo-SecureString $password -AsPlainText -Force)}'
# Replacement constants for modifying Invoke-command to apply MS hotfix in the function "Install-WindowsIdentityFoundation" $invoke_identifier="Install-WindowsIdentityFoundation" $invoke_match_start='\$psExec\s=\s\$env:dp0\+"\\PsExec.exe"' $invoke_match_end="-Wait\s-NoNewWindow" $replacement_invoke_command=@' $hotfix_file="$env:SPbits\PrerequisiteInstallerFiles\$wifHotfix" Write-Host -ForegroundColor White " - Pre-installing Windows Identity Foundation on `"$server`" ..." -NoNewline Invoke-Command {param ($value) Set-Variable -Name hotfix_file -Value $value} -ArgumentList $hotfix_file -Session $session Invoke-Command -ScriptBlock {Start-Process -FilePath "$PSHOME\powershell.exe" -ArgumentList "`"$hotfix_file`" /quiet /norestart" -Wait -NoNewWindow} -Session $session Write-Host "Done" '@
# # INITIALIZATION DeleteFile $help_function_file $sr = new-object System.IO.StreamReader("$function_file") $sw = new-object System.IO.StreamWriter("$help_function_file")
# # Read file and Write file with modification try { [int]$init_count=50 [int]$count=$init_count
# Function match [bool]$start_function_match=$false # Password code match [bool]$start_match=$false [int]$init_match_ttl=8 [int]$match_ttl=0 # invoke-command match [bool]$start_match_invoke=$false [int]$init_match_invoke_ttl=80 [int]$match_invoke_ttl=0 [bool]$start_omit_line=$false
[string]$input=$null while( ($line = $sr.ReadLine()) -ne $null) { $input=$line # If record indicates a match, write the replacement to the target file if($line -cmatch "$function_identifier") { $start_function_match=$true }
if($start_function_match) { if($line -cmatch "$function_end_identifier") { $start_function_match=$false } } else { # Not the functions that are needed. Ignore. continue }
if ($line -cmatch "$password_identifier") { $start_match=$true $match_ttl=$init_match_ttl }
if($line -cmatch "$invoke_identifier") { $start_match_invoke=$true $match_invoke_ttl=$init_match_invoke_ttl }
if (($line -cmatch "$password_match") -and $start_match) { $input=$replacement_password $start_match=$false }
if(($line -cmatch "$invoke_match_start") -and $start_match_invoke) { $start_omit_line=$true } elseif(($line -cmatch "$invoke_match_end") -and $start_match_invoke) { $start_omit_line=$false $start_match_invoke=$false $input=$replacement_invoke_command }
if($start_omit_line) { $input=$null }
# Write to current Stream Writer Object if(!([string]::IsNullOrEmpty($input))) { $sw.writeline("$input") }
if ($count-- -lt 0) { $sw.Flush() $count=$init_count } if ($start_match) { if ($match_ttl-- -lt 0) { $start_match=$false } }
if ($start_match_invoke) { if ($match_invoke_ttl-- -lt 0) { $start_match_invoke=$false } } } } finally { # Close and Dispose of Stream Reader Object $sr.close() # Close and Dispose of Stream Writer Object $sw.close() } }
#EndRegion
#Region Compose Remote-Start PS file. It will be called in a PS session as a domain account
Function Compose-RemoteStart ([string]$filePath, [string]$domainAccount, [string]$inputFile, [string]$spInstallLocation, [string]$helpFunctionFile) { Write-Output "filePath=$filePath" Write-Output "domainAccount=$domainAccount" Write-Output "inputFile=$inputFile" Write-Output "spInstallLocation=$spInstallLocation" Write-Output "helpFunctionFile=$helpFunctionFile"
$text1 = @" param `( [string]`$domain_password=`$(throw 'Parameter input for domain user password is needed') `)
`$domain_account=`"$domainAccount`" `$inputFile=`"$inputFile`" `$sp_install_location=`"$spInstallLocation`"
#Region External Functions . $helpFunctionFile #EndRegion
"@
$text2 = @' [string]$autoSPInstaller_bin="$sp_install_location\AutoSPInstaller" [string]$targetServer="$env:COMPUTERNAME" [string]$domainAccount="$domain_account" [string]$remoteAuthPassword="$domain_password"
Set-ExecutionPolicy Bypass -Force
# Globally update all instances of "localhost" in the input file to actual local server name [xml]$xmlinput = (Get-Content $inputFile) -replace ("localhost", $env:COMPUTERNAME)
# =================================================================================== # # AutoSPInstaller - See # MAIN for what to run # # ===================================================================================
#Region Setup Paths & Environment
$env:dp0=$autoSPInstaller_bin $bits = Get-Item $env:dp0 | Split-Path -Parent # Check if SharePoint binaries are in the \SharePoint subfolder as per new folder structure If (Test-Path -Path "$bits\SharePoint\setup.exe") { $env:SPbits = $bits+"\SharePoint" } Else { Throw " - Cannot locate SharePoint binaries; please check that the files are in the \SharePoint subfolder as per new folder structure." } $env:spVer,$null = (Get-Item -Path "$env:SPbits\setup.exe").VersionInfo.ProductVersion -split "\." If (!$env:spVer) {Throw " - Cannot determine version of SharePoint setup binaries."} # Create a hash table with major version to product year mappings $spYears = @{"14" = "2010"; "15" = "2013"} $spYear = $spYears.$env:spVer $PSConfig = "$env:CommonProgramFiles\Microsoft Shared\Web Server Extensions\$env:spVer\BIN\psconfig.exe" $PSConfigUI = "$env:CommonProgramFiles\Microsoft Shared\Web Server Extensions\$env:spVer\BIN\psconfigui.exe"
$script:DBPrefix = $xmlinput.Configuration.Farm.Database.DBPrefix If (($dbPrefix -ne "") -and ($dbPrefix -ne $null)) {$script:DBPrefix += "_"} If ($dbPrefix -like "*localhost*") {$script:DBPrefix = $dbPrefix -replace "localhost","$env:COMPUTERNAME"}
#EndRegion
#Region VALIDATION FUNCTIONS
Function check_error { if ($error.count -gt 0) { error_exit $args[0] } }
Function error_exit { echo $args[0] exit 1 } #EndRegion
#Region "Remotely " install for the Domain Account
Function Install-With-Domain { If (!$env:RemoteStartDate) {$env:RemoteStartDate = Get-Date} Write-Host -ForegroundColor Green "-----------------------------------" Write-Host -ForegroundColor Green "| Automated SP$spYear Remote Installs |" Write-Host -ForegroundColor Green "| Started on: $env:RemoteStartDate |" Write-Host -ForegroundColor Green "-----------------------------------"
# Launch farm server install WriteLine Write-Host -ForegroundColor Green " - Server: $server" $Error.Clear() Test-ServerConnection -Server $server check_error "- ERROR: Test-ServerConnection failed." $Error.Clear() Install-NetFramework -Server $server -Password $password check_error "- ERROR: Install-NetFramework failed" $Error.Clear() Install-WindowsIdentityFoundation -Server $server -Password $password check_error "- ERROR: Install-WindowsIdentityFoundation failed" $Error.Clear() Start-RemoteInstaller -Server $server -Password $password -InputFile $inputFile check_error "- ERROR: Start-RemoteInstaller failed" $Error.Clear()
# Finalize the installation $env:EndDate = Get-Date Write-Host -ForegroundColor Green "-----------------------------------" Write-Host -ForegroundColor Green "| Automated SP$spYear remote installs |" Write-Host -ForegroundColor Green "| Started on: $env:RemoteStartDate |" Write-Host -ForegroundColor Green "| Completed: $env:EndDate |" Write-Host -ForegroundColor Green "-----------------------------------" } #EndRegion
#Region MAIN - Check for input file and start the install
$server = $targetServer
## Verify domain account login $password = $remoteAuthPassword
# Take advantage of Install-Remote to install farm server on the local machine with Domain account. Install-With-Domain
## Installation is done. Write-Host -ForegroundColor White " - Completed!"
#EndRegion '@
DeleteFile $filePath Write-Output $text1 | Out-File $filePath Write-Output $text2 | Out-File $filePath -Append }
#EndRegion
#Region Main process
$appd_session=$null $aborted=$false
$Error.Clear() $sec_password = ConvertTo-SecureString $account_password -asPlainText -Force $credential = New-Object System.Management.Automation.PsCredential -ArgumentList $account,$sec_password
# Issue: there is some moments that the AutoSPInstaller setup failed due to # "Microsoft.Office.Server.Search.Cmdlet.SetSearchS SPUpdatedConcurrencyException" thrown (update conflict) # during Search Service Application installation. An setup re-run normally fixes the issue. We use # this solution until further notice is given.
[int]$runCount=2 [bool]$reRun=$true while ($reRun -and ($runCount-- -gt 0)) { try { # Preparation $Error.Clear() Set-RemoteSettings Set-ClientCredSSP Install-WindowsIdentityFoundation $sp_install_location Compose-HelpPSFile -AutoSPInstallerFunctionsFile "$original_function_file" -ModifiedFunctionsFile "$help_function_file" Compose-RemoteStart -FilePath "$script_file" -DomainAccount "$account" -InputFile "$inputFile" -SpInstallLocation "$sp_install_location" -HelpFunctionFile "$help_function_file"
# Start to install SP cluster server $appd_session=New-PSSession -Name "AppD-Session" -Authentication Credssp -Credential $credential -ComputerName $env:COMPUTERNAME Invoke-Command {param ($value) Set-Variable -Name script_file -Value $value} -ArgumentList $script_file -Session $appd_session Invoke-Command {param ($value) Set-Variable -Name account_password -Value $value} -ArgumentList $account_password -Session $appd_session Invoke-Command -ScriptBlock {powershell.exe $script_file $account_password} -Session $appd_session Remove-PSSession -Session $appd_session } catch { Write-Output $_ } finally { # Clean up the environment after the installation Write-Output "Cleanup after the installation..." if(!([string]::IsNullOrEmpty($appd_session))) { Remove-PSSession -Session $appd_session } Disable-WSManCredSSP -Role client Disable-WSManCredSSP -Role server
$reRun=Need-ReRun } }
# Report the errors check_error "-Error: Error(s) accurred during the whole installation." Write-Output "DONE."
#EndRegion

supported-os-details.exec-script:
$error.clear()
# Start of Json Parser
$script:tokens=New-Object System.Collections.Queue
$script:token=""
$script:pathValueMap=@{}  #HashTable between all keypath and mapping string value
$script:pathArray=@()     #HashTable is unsorted, so we need path Array which save sorted keys.

function addMatchInfoIntoToken($matches) {
    foreach ($match in $matches.matches) {
        $matchValue = $match.value
        if ( !($matchValue -match "^$SPACE$") ) { $script:tokens.Enqueue($matchValue) }
    }
}

function readToken() {
    $script:token=$script:tokens.Dequeue()
}

function tokenize($jsonString) {
    $ESCAPE = '(\\[^u]|\\u[0-9a-fA-F]{4})'
    $CHAR = '[^"\\]'
    $STRING = "`"$CHAR*($ESCAPE$CHAR*)*`""
    $NUMBER = '-?(0|[1-9][0-9]*)([.][0-9]*)?([eE][+-]?[0-9]*)?'
    $KEYWORD = 'null|false|true'
    $SPACE = '[ \t\r\n\v\f]+'
    $TOKENPATTERN = "$STRING|$NUMBER|$KEYWORD|$SPACE|."

    $script:tokens=New-Object System.Collections.Queue

    $m = ($jsonString | select-string -pattern $TOKENPATTERN -AllMatches)
    if ($m.length) {
        foreach ( $ml in $m ) {
            addMatchInfoIntoToken $ml
        }
    } else {
        addMatchInfoIntoToken $m
    }
}

function parse_array() {
    $index = 0
    $ary = ''
    readToken
    switch ($script:token) {
        "]" {}
        default {
            :lblWhile while ($true) {
                parse_value $args[0] "[$index]"
                $index = $index+1
                $ary = "$ary$script:value"
                readToken
                switch ($script:token) {
                    "]" { break lblWhile }
                    "," { $ary = "$ary,"; break }
                    default {
                        throw "EXPECTED , or ] GOT $script:token"
                    }
                }
                readToken
            }
        }
    }
    $script:value = "[$ary]"
}

function parse_object() {
    $obj = ''
    readToken
    switch ($script:token) {
        "}" { break;}
        default {
            :lblwhile while ($true) {
                if ($script:token -match "`".*`"") {
                    $key = $script:token;
                } else {
                    throw "EXPECTED string GOT $script:token"
                }
                readToken
                if ($script:token -eq ":") {
                    #DoNothing
                } else {
                    throw "EXPECTED : GOT $script:token"
                }
                readToken
                $subPath = $key.substring(1, $key.length-2) #For remove leading and tailing double quotation
                parse_value $args[0] "$subPath"
                $obj= "$obj$($key):$script:value"
                readToken
                switch ($script:token) {
                    "}" { break lblwhile }
                    "," {$obj = "$obj," ; break}
                    default {
                        throw "EXPECTED , or } GOT $script:token"
                    }
                }
                readToken
            }
        }
    }
    $script:value="{$obj}"
}

function parse_value() {
    if ($args[0]) {
        if ($args[1].StartsWith("["))  {
            $jpath = "$($args[0])$($args[1])"
        } else {
            $jpath = "$($args[0]).$($args[1])"
        }
    } else {
        $jpath = $args[1]
    }

    switch -regex ($script:token) {
        "^{$" { parse_object "$jpath" ; break }
        "^\[$" { parse_array "$jpath" ; break }
        # At this location, the only valid single character tokens are digitals.
        "^[^0-9]$" {
            throw "EXPECTED value GOT $script:token"
        }
        default {
            $script:value = $script:token
        }
    }
    if ( $script:value -eq '') { return }
    if ($jpath) {
        $script:pathValueMap.Add($jpath, $script:value)
        $script:pathArray+=$jpath
    } else {
        $script:pathValueMap.Add("", $script:value)
        $script:pathArray+=""
    }
}

function parse($jsonString) {
    $script:pathValueMap = @{}
    $script:pathArray=@()
    tokenize($jsonString)
    readToken
    parse_value
}

function getStringValueFromJson($jsonString, $keyPath) {
    parse $jsonString
    if ($script:pathValueMap.$keyPath) {
        return $script:pathValueMap.$keyPath
    } else {
        throw "keyPath $keyPath not found in JSON String"
    }
}

#
# This method will convert an JSON string to a powershell "object"
# A powershell "object" can be either a String or an array
#
# Note: this is a PRIVATE method, don't call it directly, unless you clear understand
# the impact to Json Parser private global variable $pathValueMap
#
function convertToScriptObject($stringValue, $keyPath) {
    if (!($stringValue.startsWith("["))) {
        return convertToScriptString($stringValue)
    } else {
        $arrayValue = @()
        foreach ($key in $script:pathArray) {
            if ($key.startsWith($keyPath)) {
                $subKey = $key.subString($keyPath.length)
                 if ($subKey -match  "^\[[0-9]+\]$" ) {
                     $arrayValue += convertToScriptString $script:pathValueMap.$key
                 }
            }
        }
        return ,$arrayValue
    }
}

#
# This method will convert an JSON string to PowershellString
# for example, automatically remove enclosing double quotation of JSON string
#
# @getValueFromJson method will call this method automatically
# so in most case user needn't call this method explicitly
#
function convertToScriptString($stringValue) {
    switch -regex ($stringValue) {
        "^true$" {return $true}
        "^false$" {return $false}
        "^null$" {return $null}
        '^".*"$' {
            $stringValue = $stringValue.subString(1, $stringValue.length -2)
            $stringValue = $stringValue -replace '\\([^u])', "`$1"
            # TODO We do not support \udddd format characer so far.
            # To be support after we have I18n DBCS solution.
            return $stringValue
            }
        default {return $stringValue}
    }
}


#
# This method will parse JSON String, return value of given JSON keypath.
# The returned value type will be:
#     String: whenever (1) the value of given JSON keypath is a JSON string (enclosed by double quotation)
#                   or (2) the value of given JSON keypath is a JSON object (enclosed by brace)
#     Number:  whenever the value of given JSON keypath is a number
#     Keyword: whenever the value of given JSON keypath is true/false/null
#     Array: whenever the value of given JSON keypath is a JSON array (enclosed by square)
#
# This method will automatically remove enclosing double quotation when return String or Array of String
#
# Example:
#     getValueFromJson '{"key1":"value1", "key2":{"subkey1":"subValue1"}}' 'key2.key2'
#     will return "subValue1"
# Example:
#     getValueFromJson '{"key1":["value1","value2"]}' 'key1[1]'
#     will return "value2"
# Example:
#     getValueFromJson '[{"key1":"value1"}, {"key1":"value2"}]' '[1].key1'
#     will return "value2"
# Example:
#     getValueFromJson '[{"key1":"value1"}, {"key1":"value2"}]' ''
#     will return an array with 2 elements.
#     first element is  '{"key1":"value1"}'
#     second element is '{"key1":"value2"}'
# Example:
#     getValueFromJson '{"key1":"value1"}' ''
#     $JSONVALUE will be same as input string.
#
function getValueFromJson($jsonString, $keyPath) {
    $stringValue = getStringValueFromJson "$jsonString" "$keyPath"
    $value = convertToScriptObject "$stringValue" "$keyPath"
    return ,$value
}

#End of Json Parser

#
# Convert JsonString to Powershell PSObject
# each element of target PSObject is a String or Array of String
# this method is dedicated designed for enrivonment variable setup
# Maybe we should move this method into another PS file?
#
function convertToPSObject ($jsonString) {

    $envObject = New-Object PSObject
    $strValue = getValueFromJson($jsonString, "")
    foreach ($envName in $script:pathArray) {
        if (($envName -match "^.*[\.\[\]]+.*") -or ($envName -eq "")) {
            continue
        } else {
            $envValue = convertToScriptObject $script:pathValueMap.$envName $envName
            $envObject | add-member -MemberType NoteProperty $envname  $envValue
        }
    }
    return ,$envObject
}


$vmw_appd_json_file = Get-Content "$($Args[1])"
$vmw_appd_json_obj = convertToPSObject $vmw_appd_json_file
$vmw_appd_json_props = $vmw_appd_json_obj.PsObject.Properties

# Assigning obj to local variable
ForEach ($vmw_appd_obj_prop in $vmw_appd_json_props)
{
 $vmw_appd_obj_prop_name = $vmw_appd_obj_prop.Name;
 #$vmw_appd_obj_prop_name_full = '$vmw_appd_json_obj' + '.' + $vmw_appd_obj_prop_name
 #$vmw_appd_obj_prop_value = Invoke-Expression $vmw_appd_obj_prop_name_full
 Set-Variable -Name $vmw_appd_obj_prop_name -value $vmw_appd_json_obj."$vmw_appd_obj_prop_name"
}


# Execute user's Powershell script
$error.clear()
$global:LASTEXITCODE = $null
. "$($Args[0])" 2>&1 | out-file -encoding Default "$($Args[3])"

if ($LASTEXITCODE -and $LASTEXITCODE -ne 0) {
 "`nABORT. LASTEXITCODE is non-zero.`n" | out-file -encoding Default -append $($Args[3])
 exit $LASTEXITCODE
}

if ($error.count -gt 0) {
 "`nABORT. Encountered error in Powershell.`n" | out-file -encoding Default -append $($Args[3])
 exit $error.count
 }

# Export Powershell variables to output properties file
ForEach ($vmw_appd_obj_prop in $vmw_appd_json_props)
{
 $vmw_appd_obj_prop_name = $vmw_appd_obj_prop.Name;
 $vmw_appd_obj_prop_type = (($vmw_appd_json_obj.($vmw_appd_obj_prop_name)).GetType()).Name

 # Export only String AppD's properties; Output Array properties is not currently supported.
 if ($vmw_appd_obj_prop_type.Contains("String")) {
      $vmw_appd_obj_prop_name_expanded = '$' + $vmw_appd_obj_prop_name

      # The ExpandString takes care of three cases. 
      # Case-I when the string is set via input properties
      # Case-II when the string is set via input properties and overridden in script (install/configure..)
      # Case-III lastly when it is only set via script (install/configure..) and left empty in input props
      #
      # Also note that powershell as is does not care if this variable was a computed 
      # or a string variable that filter happens at a different layer.
      #
      $expandedUserVariable = $ExecutionContext.InvokeCommand.ExpandString($vmw_appd_obj_prop_name_expanded)

     ($vmw_appd_obj_prop_name + '=' + $expandedUserVariable) | Add-Content $($Args[2]) 
 }
}

ns9:component-version-script.script:
# Action: Join Domain ####################################################################
# PROPERTIES # # VALIDATION FUNCTIONS # Function check_error { if ($error.count -gt 0) { error_exit $args[0] } }
Function error_exit { echo $args[0] exit $lastexitcode }
# JOIN THE DOMAIN #GETTING THE MACHINE NAME $domainjoin=[System.Net.Dns]::GetHostName() Write-Output "Machine's registered host name: $domainjoin"
#ADDING THE MACHINE TO THE DOMAIN. REBOOT IS NEEDED. $domain,$domain_username = $domain_acct -Split "\\" Write-Output "Join in the domain `"$domain\$domain_username`"" netdom join "$domainjoin" /domain:$domain /userd:$domain_username /passwordd:$domain_password check_error "ERROR:WHILE ADDING MACHINE TO DOMAIN" Write-Output "MACHINE ADDED TO THE DOMAIN"

