supported-os-details.exec-script:
$error.clear()
# Auto convert input properties to local Powershell variables

# Parsing JSON elements into Powershell object
function ParseJsonProps ($jsonFile)
{
 $jsonjsonCharCount = 0
 $newObjStart = $false
 $arrayStart = $false
 $tokenStart = $false
 $valueStart = $false
 $escapeStart = $false

 $jsonFileLen = $jsonFile.length
 $jsonObj = New-Object System.Text.StringBuilder

 ForEach($c in $jsonFile.ToCharArray()) {
    $jsonCharCount++
    $tmpStr = ""
    switch -regex ($c) {
        "{" { 
            if($newObjStart -eq $false) {
                $tmpStr = "(New-Object PSObject "
                $newObjStart = $true
            } else {
                $tmpStr = $c
            }
        }
        "}" {
            if($jsonCharCount -lt $jsonFileLen) {
                $tmpStr = $c
            } else {
                $tmpStr = ")"
            }
        }
        '"' {
            if($tokenStart -eq $false -and $valueStart -eq $false -and $arrayStart -eq $false) {
                $tmpStr = '| Add-Member -Passthru NoteProperty "'
            } else {
                if($escapeStart -eq $false) {
                    $tmpStr = $c
                } else {
                    $tmpStr = "```""
                    $escapeStart = $false
                }
            }
            $tokenStart = $true
        }
        ":" {
            if($valueStart -eq $true -or $arrayStart -eq $true) {
                $tmpStr = $c
            } else {
                $tmpStr = " "
            }
            $valueStart = $true
        }
        "," {
            if($arrayStart) {
                $tmpStr = $c
            } else {
                $valueStart = $false
                $tokenStart = $false
            }
        }	
        "\[" {
            $tmpStr = "@("
            $arrayStart = $true
        }
        "\]" {
            $tmpStr = ")"
            $arrayStart = $false
        }
        "``" {
            $tmpStr = "````"
        }
        "\\" {
            if($escapeStart -eq $false) {
                $escapeStart = $true
            } else {
                $tmpStr = $c
                $escapeStart = $false
            }
        }
        "\$" {
            if($escapeStart -eq $false) {
                $tmpStr = '`$'
            } else {
                $tmpStr = '`$'
                $escapeStart = $false
            }
        }
        "n" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``n"
                $escapeStart = $false
            }
        }
        "r" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``r"
                $escapeStart = $false
            }
        }
        "t" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``t"
                $escapeStart = $false
            }
        }
        "b" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``b"
                $escapeStart = $false
            }
        }
        default {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "\$c"
                $escapeStart = $false
            }
            #$tmpStr = $c
            #if($escapeStart -eq $true) {
            #    $escapeStart = $false
            #}
        }
    }                        
    [void] $jsonObj.Append($tmpStr)
 }

 $jsonObj | Invoke-Expression
}

$vmw_appd_json_file = Get-Content "$($Args[1])"
$vmw_appd_json_obj = ParseJsonProps $vmw_appd_json_file
$vmw_appd_json_props = $vmw_appd_json_obj.PsObject.Properties

# Assigning obj to local variable
ForEach ($vmw_appd_obj_prop in $vmw_appd_json_props)
{
 $vmw_appd_obj_prop_name = $vmw_appd_obj_prop.Name;
 #$vmw_appd_obj_prop_name_full = '$vmw_appd_json_obj' + '.' + $vmw_appd_obj_prop_name
 #$vmw_appd_obj_prop_value = Invoke-Expression $vmw_appd_obj_prop_name_full
 Set-Variable -Name $vmw_appd_obj_prop_name -value $vmw_appd_json_obj."$vmw_appd_obj_prop_name"
}


# Execute user's Powershell script
$error.clear()
$LASTEXITCODE = 0
. "$($Args[0])" 2>&1 | out-file -encoding Default "$($Args[3])"

if ($LASTEXITCODE -ne 0) {
 "`nABORT. LASTEXITCODE is non-zero.`n" | out-file -encoding Default -append $($Args[3])
 exit $LASTEXITCODE
}

if ($error.count -gt 0) {
 "`nABORT. Encountered error in Powershell.`n" | out-file -encoding Default -append $($Args[3])
 exit $error.count
 }

# Export Powershell variables to output properties file
ForEach ($vmw_appd_obj_prop in $vmw_appd_json_props)
{
 $vmw_appd_obj_prop_name = $vmw_appd_obj_prop.Name;
 $vmw_appd_obj_prop_type = (($vmw_appd_json_obj.($vmw_appd_obj_prop_name)).GetType()).Name

 # Export only String AppD's properties; Output Array properties is not currently supported.
 if ($vmw_appd_obj_prop_type.Contains("String")) {
      $vmw_appd_obj_prop_name_expanded = '$' + $vmw_appd_obj_prop_name

      # The ExpandString takes care of three cases. 
      # Case-I when the string is set via input properties
      # Case-II when the string is set via input properties and overridden in script (install/configure..)
      # Case-III lastly when it is only set via script (install/configure..) and left empty in input props
      #
      # Also note that powershell as is does not care if this variable was a computed 
      # or a string variable that filter happens at a different layer.
      #
      $expandedUserVariable = $ExecutionContext.InvokeCommand.ExpandString($vmw_appd_obj_prop_name_expanded)

     ($vmw_appd_obj_prop_name + '=' + $expandedUserVariable) | Add-Content $($Args[2]) 
 }
}

ns9:component-version-script.script:
###################################################################################################################### # Purpose: This script creates a sample website(sample_app) to demonstrate the usage of Sharepoint. # ######################################################################################################################
cls ############### INTERNAL PARAMETERTS ################# # Name of web site(sample app) $SITENAME = "$sampleapp_sitename" # sample app running port $SITEPORT = "$sampleapp_port" # Sample app pool name(Used to separate different Web applications and Web sites into groups) $APPPOOLNAME = "$apppool_name" # Sample app directory $APP_DIR = "$sample_app_dir\APP"
write-host "CREATING SAMPLE APP DIRECTORIES" if (!(Test-Path -Path $APP_DIR)) { New-Item $APP_DIR -type directory }
# Script file that will create sample app $WEBAPP_FILE = "$APP_DIR\CreateWebApp.PS1" $TEMP_FILE = "$APP_DIR\Temp.txt" # Script file that will create site pool and add web site in to it. $SITECOLLECTION_FILE = "$APP_DIR\CreateSiteCollection.PS1" # To get copmputer name. $COMPUTERNAME = get-content env:computername # User name(machine user name) $USERNAME = "$admin_user" # password (machine password) $PASSWORD = "$admin_password" # Copy the password into tmp.txt file $PASSWORD > "$APP_DIR\tmp.txt"
########VALIDATION FUNCTIONS#########################
Function check_error { if (!$?) { error_exit $args[0] } }
Function error_exit { write-host $args[0] exit 1 }
Function valid_int { $STR = $args[0] $ValidSTR = $true $ValidSTR = ($STR-match"^[0-9]{1,9}$") if(-not $ValidSTR){return $false} return $ValidSTR }
#########PARAMETER VALIDATION#########################
write-host "PARAMETER VALIDATION -- START"
if (($SITENAME -eq '')) { error_exit "ERROR: SITE NAME NOT SET" } if (($APPPOOLNAME -eq '')) { error_exit "ERROR:APP POOL NAME" } if (($APP_DIR -eq '')) { error_exit "ERROR:APP DIRECTORY NOT SET" } if (($USERNAME -eq '')) { error_exit "ERROR:USER NAME NOT SET" } if (($PASSWORD -eq '')) { error_exit "ERROR:PASSWORD NOT SET" } if (($SITEPORT -eq '')) { error_exit "ERROR:PORT NOT SET" } elseif(!(valid_int $SITEPORT)) { error_exit "ERROR:NOT A VALID LISTENING PORT" }
############ CREATING A POWERSHELL SCRIPT FILE TO CREATE A SAMPLE APP################ New-Item $WEBAPP_FILE -type file $sw1 = new-object system.IO.StreamWriter($WEBAPP_FILE) $sw1.writeline("add-PSSnapin Microsoft.Sharepoint.powershell") # define a variable $Username(combination of computer name and username) $sw1.writeline("`$Username = `"$COMPUTERNAME\$USERNAME`"") # Store password into $Password varable from tmp.txt $sw1.writeline("`$Password = get-content `"$INSTALLER\tmp.txt`"") # Make secure string from password $sw1.writeline("`$my_secure_password_string = convertto-securestring `"`$Password`" -asplaintext -force") # Change the secure string in to encrypted string by adding key in to password secure string $sw1.writeline("`$my_encrypted_string = convertfrom-securestring `$my_secure_password_string-key (1..16)") # Store encrypted string in to a txt file $sw1.writeline("`$my_encrypted_string > `"$INSTALLER\password.txt`"") # Read Encrypted password from txt file $sw1.writeline("`$my_secure_password_string = convertto-securestring (get-content `"$INSTALLER\password.txt`") -key (1..16)") # Create Credentail variable by using username and password $sw1.writeline("`$cred = New-Object System.Management.Automation.PSCredential `$Username,`$my_secure_password_string ") # Create Manage Account $sw1.writeline("New-SPManagedAccount -Credential `$cred") # Web site name $sw1.writeline("`$siteName = `"$SITENAME`"") # Web site running port $sw1.writeline("`$port = `"$SITEPORT`"") # Host header $sw1.writeline("`$hostHeader = `"`"") # Web site URL $sw1.writeline("`$url = `"http://$COMPUTERNAME`"") # Application pool name $sw1.writeline("`$appPoolName = `"$APPPOOLNAME`"") # Managed Account name $sw1.writeline("`$managedAccount = `"$COMPUTERNAME\$USERNAME`"") # DB server name $sw1.writeline("`$dbServer = `"$COMPUTERNAME\SharePoint`"") # DB name $sw1.writeline("`$dbName = `"WSS_Content_$SITEPORT`"") $sw1.writeline("`$authenticationMethod = `"NTLM`"") # Command let that will create sample app by using above declared variables $sw1.writeline("New-SPWebApplication -Name `$siteName -Port `$port -HostHeader `$hostHeader -URL `$url -ApplicationPool `$appPoolName -ApplicationPoolAccount (Get-SPManagedAccount `"`$managedAccount`") -DatabaseName `$dbName -DatabaseServer `$dbServer -AllowAnonymousAccess: `$allowAnonymous -AuthenticationMethod `$authenticationMethod") $sw1.close()
##################### EXECUTE THE Above created SCRIPT file AS AN ADMINISTRATOR#############################
Start-Process powershell -verb runas -ArgumentList "-file $WEBAPP_FILE"
############ CREATING A POWERSHELL SCRIPT FILE TO CREATE A SAMPLE POOL and the sample app into it################ New-Item $SITECOLLECTION_FILE -type file $sw = new-object system.IO.StreamWriter($SITECOLLECTION_FILE) # Add the below command let in to the starting of the script $sw.writeline("add-PSSnapin Microsoft.Sharepoint.powershell") # URL of site that we want to add in to pool $sw.writeline("`$siteURL = `"http://$COMPUTERNAME`:$SITEPORT/sites/test`"") $sw.writeline("`$owner = `"$COMPUTERNAME\$USERNAME`"") $sw.writeline("`$secondOwner = `"$COMPUTERNAME\$USERNAME`"") # type of template (we can also use some othter template) $sw.writeline("`$template = `"STS#0`"") $sw.writeline("`$description = `"`" ") # Command let that will add website in to pool. $sw.writeline("New-SPSite `$siteURL -OwnerAlias `$owner -SecondaryOwnerAlias `$secondOwner -name `"PowerShell for SharePoint`" -Template `$template -Description `$description") $sw.close() ##################### EXECUTE THE SCRIPT AS AN ADMINISTRATOR############################# Start-Process powershell -verb runas -ArgumentList "-file $SITECOLLECTION_FILE"

supported-os-details.exec-script:
$error.clear()
# Auto convert input properties to local Powershell variables

# Parsing JSON elements into Powershell object
function ParseJsonProps ($jsonFile)
{
 $jsonjsonCharCount = 0
 $newObjStart = $false
 $arrayStart = $false
 $tokenStart = $false
 $valueStart = $false
 $escapeStart = $false

 $jsonFileLen = $jsonFile.length
 $jsonObj = New-Object System.Text.StringBuilder

 ForEach($c in $jsonFile.ToCharArray()) {
    $jsonCharCount++
    $tmpStr = ""
    switch -regex ($c) {
        "{" { 
            if($newObjStart -eq $false) {
                $tmpStr = "(New-Object PSObject "
                $newObjStart = $true
            } else {
                $tmpStr = $c
            }
        }
        "}" {
            if($jsonCharCount -lt $jsonFileLen) {
                $tmpStr = $c
            } else {
                $tmpStr = ")"
            }
        }
        '"' {
            if($tokenStart -eq $false -and $valueStart -eq $false -and $arrayStart -eq $false) {
                $tmpStr = '| Add-Member -Passthru NoteProperty "'
            } else {
                if($escapeStart -eq $false) {
                    $tmpStr = $c
                } else {
                    $tmpStr = "```""
                    $escapeStart = $false
                }
            }
            $tokenStart = $true
        }
        ":" {
            if($valueStart -eq $true -or $arrayStart -eq $true) {
                $tmpStr = $c
            } else {
                $tmpStr = " "
            }
            $valueStart = $true
        }
        "," {
            if($arrayStart) {
                $tmpStr = $c
            } else {
                $valueStart = $false
                $tokenStart = $false
            }
        }	
        "\[" {
            $tmpStr = "@("
            $arrayStart = $true
        }
        "\]" {
            $tmpStr = ")"
            $arrayStart = $false
        }
        "``" {
            $tmpStr = "````"
        }
        "\\" {
            if($escapeStart -eq $false) {
                $escapeStart = $true
            } else {
                $tmpStr = $c
                $escapeStart = $false
            }
        }
        "\$" {
            if($escapeStart -eq $false) {
                $tmpStr = '`$'
            } else {
                $tmpStr = '`$'
                $escapeStart = $false
            }
        }
        "n" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``n"
                $escapeStart = $false
            }
        }
        "r" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``r"
                $escapeStart = $false
            }
        }
        "t" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``t"
                $escapeStart = $false
            }
        }
        "b" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``b"
                $escapeStart = $false
            }
        }
        default {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "\$c"
                $escapeStart = $false
            }
            #$tmpStr = $c
            #if($escapeStart -eq $true) {
            #    $escapeStart = $false
            #}
        }
    }                        
    [void] $jsonObj.Append($tmpStr)
 }

 $jsonObj | Invoke-Expression
}

$vmw_appd_json_file = Get-Content "$($Args[1])"
$vmw_appd_json_obj = ParseJsonProps $vmw_appd_json_file
$vmw_appd_json_props = $vmw_appd_json_obj.PsObject.Properties

# Assigning obj to local variable
ForEach ($vmw_appd_obj_prop in $vmw_appd_json_props)
{
 $vmw_appd_obj_prop_name = $vmw_appd_obj_prop.Name;
 #$vmw_appd_obj_prop_name_full = '$vmw_appd_json_obj' + '.' + $vmw_appd_obj_prop_name
 #$vmw_appd_obj_prop_value = Invoke-Expression $vmw_appd_obj_prop_name_full
 Set-Variable -Name $vmw_appd_obj_prop_name -value $vmw_appd_json_obj."$vmw_appd_obj_prop_name"
}


# Execute user's Powershell script
$error.clear()
$LASTEXITCODE = 0
. "$($Args[0])" 2>&1 | out-file -encoding Default "$($Args[3])"

if ($LASTEXITCODE -ne 0) {
 "`nABORT. LASTEXITCODE is non-zero.`n" | out-file -encoding Default -append $($Args[3])
 exit $LASTEXITCODE
}

if ($error.count -gt 0) {
 "`nABORT. Encountered error in Powershell.`n" | out-file -encoding Default -append $($Args[3])
 exit $error.count
 }

# Export Powershell variables to output properties file
ForEach ($vmw_appd_obj_prop in $vmw_appd_json_props)
{
 $vmw_appd_obj_prop_name = $vmw_appd_obj_prop.Name;
 $vmw_appd_obj_prop_type = (($vmw_appd_json_obj.($vmw_appd_obj_prop_name)).GetType()).Name

 # Export only String AppD's properties; Output Array properties is not currently supported.
 if ($vmw_appd_obj_prop_type.Contains("String")) {
      $vmw_appd_obj_prop_name_expanded = '$' + $vmw_appd_obj_prop_name

      # The ExpandString takes care of three cases. 
      # Case-I when the string is set via input properties
      # Case-II when the string is set via input properties and overridden in script (install/configure..)
      # Case-III lastly when it is only set via script (install/configure..) and left empty in input props
      #
      # Also note that powershell as is does not care if this variable was a computed 
      # or a string variable that filter happens at a different layer.
      #
      $expandedUserVariable = $ExecutionContext.InvokeCommand.ExpandString($vmw_appd_obj_prop_name_expanded)

     ($vmw_appd_obj_prop_name + '=' + $expandedUserVariable) | Add-Content $($Args[2]) 
 }
}

