ns9:component-version-script.script:
######################################################################################################################
# Purpose: This script installs Microsoft SharePoint Server on single node.                                          #
######################################################################################################################

cls

##############SCRIPT INTERNAL PARAMETERS#######################

$SHAREPOINT_INSTALLER = "$sharepoint_installer"
$INSTALLER = "$SHAREPOINT_INSTALLER\INSTALLER"
$LOG_DIR = "$SHAREPOINT_INSTALLER\LOG"
$DOWNLOAD_URL = "$download_url"
$CONFIG_XML="$config_xml"
$PID_KEY="$pid_key"
$SERVER_ROLE="$server_role"
$PACKAGE_ID="$package_id"
$PORT = "$port"
$MACHINE_IP = "$machine_ip"

###########CREATING INSTALLATION DIRECTORIES###################

write-host "CREATING INSTALLATION DIRECTORIES"
if (!(Test-Path -Path $INSTALLER))
{
      New-Item $INSTALLER -type directory
}
if (!(Test-Path -Path $LOG_DIR))
{
      New-Item $LOG_DIR -type directory
      Write-Host $LOG_DIR
}

########VALIDATION FUNCTIONS#########################

Function check_error
{
      if (!$?)
      { 
            error_exit $args[0] 
      }
}

Function error_exit
{
      write-host $args[0]
      exit 1
}
###### FUNCTION TO VALIDATE THE IP ########################
Function valid_ip
{
     $IP = $args[0]
     $ValidIP = $true

    <#Test format and leading zero (technically not necessary to test for
    leading zero as this function strips the leading zero when converting to [int]#>
    $ValidIP = ($IP -match "^[1-9]\d{0,2}\.\d{1,3}\.\d{1,3}\.\d{1,3}$") 
    if(-not $ValidIP){return $false}

    $myip = $IP -split "\." #Each octet is element of an array
    $ValidIP = ([int]$myip[0] -gt 0 -and [int]$myip[0] -le 255) #First octet is between 1-255
    if(-not $ValidIP){return $false}

    for($i = 1;$i -le 3;$i++)
    {
        $ValidIP = ([int]$myip[$i] -ge 0 -and [int]$myip[$i] -le 255) #Remain octets between 0-255
        if(-not $ValidIP){return $false}
    }
    return $ValidIP #$ValidIP will always be true at this point.
}
############# FUNCTION TO VALIDATE THE INTEGER VALUE###########
Function valid_int
{ 
    $STR = $args[0]
    $ValidSTR = $true
    $ValidSTR = ($STR-match"^[0-9]{1,9}$")
    if(-not $ValidSTR){return $false}
      return $ValidSTR
}

#########PARAMETER VALIDATION#########################

write-host "PARAMETER VALIDATION -- START"

if (($SHAREPOINT_INSTALLER -eq ''))
{
      error_exit "ERROR:SHAREPOINT INSTALLER NOT SET" 
}
if (($INSTALLER -eq ''))
{
      error_exit "ERROR:INSTALLER NOT SET"      
}
if (($LOG_DIR -eq ''))
{
      error_exit "ERROR:LOG DIRECTORY NOT SET"  
}
if (($DOWNLOAD_URL -eq ''))
{
      error_exit "ERROR:DOWNLOAD URL NOT SET"   
}
if (($CONFIG_XML -eq ''))
{
      error_exit "ERROR:CONFIG_XML NOT SET"     
}
if (($PID_KEY -eq ''))
{
      error_exit "ERROR:PID KEY NOT SET"  
}
if (($SERVER_ROLE -eq ''))
{
      error_exit "ERROR:SERVER ROLE NOT SET"    
}
if (($PACKAGE_ID -eq ''))
{
      error_exit "ERROR:PACKAGE ID  NOT SET"    
}
if (($PORT -eq ''))
{
      error_exit "ERROR:PORT NOT SET"     
}
elseif(!(valid_int $PORT))
{
    error_exit "ERROR:NOT A VALID LISTENING PORT"
}

if (($MACHINE_IP -eq ''))
{
      error_exit "ERROR:MACHINE IP NOT SET"     
}
elseif(!(valid_ip $MACHINE_IP))
{
    error_exit "ERROR:NOT A VALID MACHINE IP"
}


########SHAREPOINT INSTALLER DOWNLAOD###############

$WEBCLIENT = new-object system.net.webclient
$TARGET_FILE = "$INSTALLER/SharePointServer.exe"
$WEBCLIENT.Downloadfile($DOWNLOAD_URL,$TARGET_FILE)

########### EXECUTE THE SELF EXTRACTING SHAREPOINT EXE ########
cd $INSTALLER
./SharePointServer.exe
Start-Sleep -s 180
#################### PATH WHERE SHARE POINT INSTALLER PACKAGE EXTRACTED ################
$PATH = "C:\Program Files (x86)\MSECache\oserver2010"

cd $PATH
########## EXECUTING SHARE POINT PREREQUISITE INSTALLER ##########################
.\PrerequisiteInstaller.exe /unattended
Start-Sleep -s 600
######### EXECUTE THE PREREQUISITE AGAIN TO INSTALL PREREQUISITE IF ANY NOT INSTALLED PREVIOUSLY ############# 
.\PrerequisiteInstaller.exe /unattended
Start-Sleep -s 300

############CREATING CONFIG XML FILE WHICH WILL BE USED AT SHAREPOINT SILENT INSTALLATION ################ 
Remove-Item $CONFIG_XML
New-Item $CONFIG_XML -type file
$sw = new-object system.IO.StreamWriter($CONFIG_XML)
$sw.writeline("<Configuration> ")
$sw.writeline("   <Package Id=`"$PACKAGE_ID`"> ")
$sw.writeline("                 <Setting Id=`"LAUNCHEDFROMSETUPSTS`" Value=`"Yes`"/>")
$sw.writeline("   </Package> ")
$sw.writeline("         <Logging Type=`"verbose`" Path=`"$LOG_DIR`" Template=`"SharePoint Server Setup(*).log`"/> ")
$sw.writeline("         <Display Level=`"basic`" CompletionNotice=`"no`" SupressModal=`"no`" AcceptEula=`"yes`"/> ")
$sw.writeline("         <PIDKEY Value=`"$PID_KEY`" />")
$sw.writeline("         <Setting Id=`"SERVERROLE`" Value=`"$SERVER_ROLE`"/>")
$sw.writeline("         <Setting Id=`"USINGUIINSTALLMODE`" Value=`"0`"/>")
$sw.writeline("         <Setting Id=`"SETUPTYPE`" Value=`"CLEAN_INSTALL`"/>")
$sw.writeline("         <Setting Id=`"SETUP_REBOOT`" Value=`"Never`"/>")
$sw.writeline("</Configuration> ")
$sw.close()

#############INVOKE THE SHAREPOINT SETUP EXE#############
cd $path
.\setup.exe /config $CONFIG_XML
Start-Sleep -s 1800

########CREATING A POWERSHELL SCRIPT FILE TO CONFIGURE THE SHAREPOINT RUNNING PORT###############
$PORT_FILE = "$INSTALLER\Port.PS1"
New-Item $PORT_FILE -type file
$sw = new-object system.IO.StreamWriter($PORT_FILE)
$sw.writeline("cd `"C:\Program Files\Common Files\Microsoft Shared\Web Server Extensions\14\BIN`"")
$sw.writeline(".\STSADM.EXE -o setadminport -port $PORT")
$sw.close()
############# EXECUTE THE PROT.PS1 FILE AS A ADMINISTRATOR #########################
Start-Process powershell -verb runas -ArgumentList "-file $PORT_FILE"

###########FOR SUCCESSFUL EXIT CRITERIA READ THE SHAREPOINT INSTALLATION LOGS AND SEARCH FOR STRING "Return code: 0" ###############
$fileEntries = [IO.Directory]::GetFiles($LOG_DIR); 
foreach($fileName in $fileEntries) 
{ 
  if ($fileName.Contains(".log"))
        {
          $log_file = $fileName
        }

}
$SearchString = "Return code: 0"
$Sel = select-string -pattern $SearchString -path $log_file
If ($Sel -eq $null) 
{ 
   echo "SHAREPOINT SERVER NOT INSTALLED SUCCESSFULLY "
} 
Else
{
    echo "SHAREPOINT SERVER INSTALLED SUCCESSFULLY "

}

Start-Sleep -s 180
###########FOR SECOND SUCCESSFUL EXIT CRITERIA WE ARE CHECKING THAT SHAREPOINT SERVICE RUNING AT GIVEN PORT ###############
$Socket = New-Object Net.Sockets.TcpClient
$Socket.Connect("$MACHINE_IP","$PORT")
if ($Socket.Connected) 
{
   Write-Host "SharePoint Service Running At port $PORT " 
   $Socket.Close()
   exit
}
else
{
  Write-Host "SharePoint Service Not Running At port $PORT " 
  exit
}

supported-os-details.exec-script:
$error.clear()
# Start of Json Parser
$script:tokens=New-Object System.Collections.Queue
$script:token=""
$script:pathValueMap=@{}  #HashTable between all keypath and mapping string value
$script:pathArray=@()     #HashTable is unsorted, so we need path Array which save sorted keys.

function addMatchInfoIntoToken($matches) {
    foreach ($match in $matches.matches) {
        $matchValue = $match.value
        if ( !($matchValue -match "^$SPACE$") ) { $script:tokens.Enqueue($matchValue) }
    }
}

function readToken() {
    $script:token=$script:tokens.Dequeue()
}

function tokenize($jsonString) {
    $ESCAPE = '(\\[^u]|\\u[0-9a-fA-F]{4})'
    $CHAR = '[^"\\]'
    $STRING = "`"$CHAR*($ESCAPE$CHAR*)*`""
    $NUMBER = '-?(0|[1-9][0-9]*)([.][0-9]*)?([eE][+-]?[0-9]*)?'
    $KEYWORD = 'null|false|true'
    $SPACE = '[ \t\r\n\v\f]+'
    $TOKENPATTERN = "$STRING|$NUMBER|$KEYWORD|$SPACE|."

    $script:tokens=New-Object System.Collections.Queue

    $m = ($jsonString | select-string -pattern $TOKENPATTERN -AllMatches)
    if ($m.length) {
        foreach ( $ml in $m ) {
            addMatchInfoIntoToken $ml
        }
    } else {
        addMatchInfoIntoToken $m
    }
}

function parse_array() {
    $index = 0
    $ary = ''
    readToken
    switch ($script:token) {
        "]" {}
        default {
            :lblWhile while ($true) {
                parse_value $args[0] "[$index]"
                $index = $index+1
                $ary = "$ary$script:value"
                readToken
                switch ($script:token) {
                    "]" { break lblWhile }
                    "," { $ary = "$ary,"; break }
                    default {
                        throw "EXPECTED , or ] GOT $script:token"
                    }
                }
                readToken
            }
        }
    }
    $script:value = "[$ary]"
}

function parse_object() {
    $obj = ''
    readToken
    switch ($script:token) {
        "}" { break;}
        default {
            :lblwhile while ($true) {
                if ($script:token -match "`".*`"") {
                    $key = $script:token;
                } else {
                    throw "EXPECTED string GOT $script:token"
                }
                readToken
                if ($script:token -eq ":") {
                    #DoNothing
                } else {
                    throw "EXPECTED : GOT $script:token"
                }
                readToken
                $subPath = $key.substring(1, $key.length-2) #For remove leading and tailing double quotation
                parse_value $args[0] "$subPath"
                $obj= "$obj$($key):$script:value"
                readToken
                switch ($script:token) {
                    "}" { break lblwhile }
                    "," {$obj = "$obj," ; break}
                    default {
                        throw "EXPECTED , or } GOT $script:token"
                    }
                }
                readToken
            }
        }
    }
    $script:value="{$obj}"
}

function parse_value() {
    if ($args[0]) {
        if ($args[1].StartsWith("["))  {
            $jpath = "$($args[0])$($args[1])"
        } else {
            $jpath = "$($args[0]).$($args[1])"
        }
    } else {
        $jpath = $args[1]
    }

    switch -regex ($script:token) {
        "^{$" { parse_object "$jpath" ; break }
        "^\[$" { parse_array "$jpath" ; break }
        # At this location, the only valid single character tokens are digitals.
        "^[^0-9]$" {
            throw "EXPECTED value GOT $script:token"
        }
        default {
            $script:value = $script:token
        }
    }
    if ( $script:value -eq '') { return }
    if ($jpath) {
        $script:pathValueMap.Add($jpath, $script:value)
        $script:pathArray+=$jpath
    } else {
        $script:pathValueMap.Add("", $script:value)
        $script:pathArray+=""
    }
}

function parse($jsonString) {
    $script:pathValueMap = @{}
    $script:pathArray=@()
    tokenize($jsonString)
    readToken
    parse_value
}

function getStringValueFromJson($jsonString, $keyPath) {
    parse $jsonString
    if ($script:pathValueMap.$keyPath) {
        return $script:pathValueMap.$keyPath
    } else {
        throw "keyPath $keyPath not found in JSON String"
    }
}

#
# This method will convert an JSON string to a powershell "object"
# A powershell "object" can be either a String or an array
#
# Note: this is a PRIVATE method, don't call it directly, unless you clear understand
# the impact to Json Parser private global variable $pathValueMap
#
function convertToScriptObject($stringValue, $keyPath) {
    if (!($stringValue.startsWith("["))) {
        return convertToScriptString($stringValue)
    } else {
        $arrayValue = @()
        foreach ($key in $script:pathArray) {
            if ($key.startsWith($keyPath)) {
                $subKey = $key.subString($keyPath.length)
                 if ($subKey -match  "^\[[0-9]+\]$" ) {
                     $arrayValue += convertToScriptString $script:pathValueMap.$key
                 }
            }
        }
        return ,$arrayValue
    }
}

#
#This method will convert an escaped Json char to a powershell char
#
function escapeJsonChar($jsonChar) {
    switch ($jsonChar) {
        '"' { return '"';};
        '/' { return "`/";};
        'b' { return "`b";};
        'f' { return "`f";};
        'r' { return "`r";};
        't' { return "`t";};
        'n' { return "`n";};
        '\' { return "`\";};
        default { return $jsonChar }
    }
}

#
# This method will convert an JSON string to PowershellString
# for example, automatically remove enclosing double quotation of JSON string
# 
# @getValueFromJson method will call this method automatically
# so in most case user needn't call this method explicitly
#
function convertToScriptString($stringValue) {
    switch -regex ($stringValue) {
        "^true$" {return $true}
        "^false$" {return $false}
        "^null$" {return $null}
        '^".*"$' {
            $stringValue = $stringValue.subString(1, $stringValue.length -2)
            $stringValue2 = ""
            $isEscaping = $False;
            foreach ($char in $stringValue.toCharArray()) {
                if ($isEscaping) {
                    # TODO We do not support \udddd format characer so far.
                    # To be support after we have I18n DBCS solution.
                    $isEscaping = $False;
                    $stringValue2 += escapeJsonChar($char)
                } else {
                    if ($char.Equals("\".toCharArray()[0])) {
                        $isEscaping = $True;
                        continue;
                    } else {
                        $isEscaping = $False;
                        $stringValue2 += $char
                    }
                }
            }
            return $stringValue2
        }
        default {return $stringValue}
    }
}

#
# This method will parse JSON String, return value of given JSON keypath.
# The returned value type will be:
#     String: whenever (1) the value of given JSON keypath is a JSON string (enclosed by double quotation)
#                   or (2) the value of given JSON keypath is a JSON object (enclosed by brace)
#     Number:  whenever the value of given JSON keypath is a number
#     Keyword: whenever the value of given JSON keypath is true/false/null
#     Array: whenever the value of given JSON keypath is a JSON array (enclosed by square)
#
# This method will automatically remove enclosing double quotation when return String or Array of String
#
# Example:
#     getValueFromJson '{"key1":"value1", "key2":{"subkey1":"subValue1"}}' 'key2.key2'
#     will return "subValue1"
# Example:
#     getValueFromJson '{"key1":["value1","value2"]}' 'key1[1]'
#     will return "value2"
# Example:
#     getValueFromJson '[{"key1":"value1"}, {"key1":"value2"}]' '[1].key1'
#     will return "value2"
# Example:
#     getValueFromJson '[{"key1":"value1"}, {"key1":"value2"}]' ''
#     will return an array with 2 elements.
#     first element is  '{"key1":"value1"}'
#     second element is '{"key1":"value2"}'
# Example:
#     getValueFromJson '{"key1":"value1"}' ''
#     $JSONVALUE will be same as input string.
#
function getValueFromJson($jsonString, $keyPath) {
    $stringValue = getStringValueFromJson "$jsonString" "$keyPath"
    $value = convertToScriptObject "$stringValue" "$keyPath"
    return ,$value
}

#End of Json Parser

#
# Convert JsonString to Powershell PSObject
# each element of target PSObject is a String or Array of String
# this method is dedicated designed for enrivonment variable setup
# Maybe we should move this method into another PS file?
#
function convertToPSObject ($jsonString) {

    $envObject = New-Object PSObject
    $strValue = getValueFromJson($jsonString, "")
    foreach ($envName in $script:pathArray) {
        if (($envName -match "^.*[\.\[\]]+.*") -or ($envName -eq "")) {
            continue
        } else {
            $envValue = convertToScriptObject $script:pathValueMap.$envName $envName
            $envObject | add-member -MemberType NoteProperty $envname  $envValue
        }
    }
    return ,$envObject
}


$vmw_appd_json_file = Get-Content "$($Args[1])"
$vmw_appd_json_obj = convertToPSObject $vmw_appd_json_file
$vmw_appd_json_props = $vmw_appd_json_obj.PsObject.Properties

# Assigning obj to local variable
ForEach ($vmw_appd_obj_prop in $vmw_appd_json_props)
{
 $vmw_appd_obj_prop_name = $vmw_appd_obj_prop.Name;
 #$vmw_appd_obj_prop_name_full = '$vmw_appd_json_obj' + '.' + $vmw_appd_obj_prop_name
 #$vmw_appd_obj_prop_value = Invoke-Expression $vmw_appd_obj_prop_name_full
 Set-Variable -Name $vmw_appd_obj_prop_name -value $vmw_appd_json_obj."$vmw_appd_obj_prop_name"
}


# Execute user's Powershell script
$error.clear()
$global:LASTEXITCODE = $null
. "$($Args[0])" 2>&1 | out-file -encoding Default "$($Args[3])"

if ($LASTEXITCODE -and $LASTEXITCODE -ne 0) {
 "`nABORT. LASTEXITCODE is non-zero.`n" | out-file -encoding Default -append $($Args[3])
 exit $LASTEXITCODE
}

if ($error.count -gt 0) {
 "`nABORT. Encountered error in Powershell.`n" | out-file -encoding Default -append $($Args[3])
 exit $error.count
 }

# Export Powershell variables to output properties file
ForEach ($vmw_appd_obj_prop in $vmw_appd_json_props)
{
 $vmw_appd_obj_prop_name = $vmw_appd_obj_prop.Name;
 $vmw_appd_obj_prop_type = (($vmw_appd_json_obj.($vmw_appd_obj_prop_name)).GetType()).Name

 # Export only String AppD's properties; Output Array properties is not currently supported.
 if ($vmw_appd_obj_prop_type.Contains("String")) {
      $vmw_appd_obj_prop_name_expanded = '$' + $vmw_appd_obj_prop_name

      # The ExpandString takes care of three cases. 
      # Case-I when the string is set via input properties
      # Case-II when the string is set via input properties and overridden in script (install/configure..)
      # Case-III lastly when it is only set via script (install/configure..) and left empty in input props
      #
      # Also note that powershell as is does not care if this variable was a computed 
      # or a string variable that filter happens at a different layer.
      #
      $expandedUserVariable = $ExecutionContext.InvokeCommand.ExpandString($vmw_appd_obj_prop_name_expanded)

     ($vmw_appd_obj_prop_name + '=' + $expandedUserVariable) | Add-Content $($Args[2]) 
 }
}

ns9:component-version-script.script:
###################################################################################################################### # Purpose: This script creates a sample website(sample_app) to demonstrate the usage of Sharepoint. # ######################################################################################################################
cls ############### INTERNAL PARAMETERTS ################# # Name of web site(sample app) $SITENAME = "$sampleapp_sitename" # sample app running port $SITEPORT = "$sampleapp_port" # Sample app pool name(Used to separate different Web applications and Web sites into groups) $APPPOOLNAME = "$apppool_name" # Sample app directory $APP_DIR = "$sample_app_dir\APP"
write-host "CREATING SAMPLE APP DIRECTORIES" if (!(Test-Path -Path $APP_DIR)) { New-Item $APP_DIR -type directory }
# Script file that will create sample app $WEBAPP_FILE = "$APP_DIR\CreateWebApp.PS1" $TEMP_FILE = "$APP_DIR\Temp.txt" # Script file that will create site pool and add web site in to it. $SITECOLLECTION_FILE = "$APP_DIR\CreateSiteCollection.PS1" # To get copmputer name. $COMPUTERNAME = get-content env:computername # User name(machine user name) $USERNAME = "$admin_user" # password (machine password) $PASSWORD = "$admin_password" # Copy the password into tmp.txt file $PASSWORD > "$APP_DIR\tmp.txt"
########VALIDATION FUNCTIONS#########################
Function check_error { if (!$?) { error_exit $args[0] } }
Function error_exit { write-host $args[0] exit 1 }
Function valid_int { $STR = $args[0] $ValidSTR = $true $ValidSTR = ($STR-match"^[0-9]{1,9}$") if(-not $ValidSTR){return $false} return $ValidSTR }
#########PARAMETER VALIDATION#########################
write-host "PARAMETER VALIDATION -- START"
if (($SITENAME -eq '')) { error_exit "ERROR: SITE NAME NOT SET" } if (($APPPOOLNAME -eq '')) { error_exit "ERROR:APP POOL NAME" } if (($APP_DIR -eq '')) { error_exit "ERROR:APP DIRECTORY NOT SET" } if (($USERNAME -eq '')) { error_exit "ERROR:USER NAME NOT SET" } if (($PASSWORD -eq '')) { error_exit "ERROR:PASSWORD NOT SET" } if (($SITEPORT -eq '')) { error_exit "ERROR:PORT NOT SET" } elseif(!(valid_int $SITEPORT)) { error_exit "ERROR:NOT A VALID LISTENING PORT" }
############ CREATING A POWERSHELL SCRIPT FILE TO CREATE A SAMPLE APP################ New-Item $WEBAPP_FILE -type file $sw1 = new-object system.IO.StreamWriter($WEBAPP_FILE) $sw1.writeline("add-PSSnapin Microsoft.Sharepoint.powershell") # define a variable $Username(combination of computer name and username) $sw1.writeline("`$Username = `"$COMPUTERNAME\$USERNAME`"") # Store password into $Password varable from tmp.txt $sw1.writeline("`$Password = get-content `"$APP_DIR\tmp.txt`"") # Make secure string from password $sw1.writeline("`$my_secure_password_string = convertto-securestring `"`$Password`" -asplaintext -force") # Change the secure string in to encrypted string by adding key in to password secure string $sw1.writeline("`$my_encrypted_string = convertfrom-securestring `$my_secure_password_string-key (1..16)") # Store encrypted string in to a txt file $sw1.writeline("`$my_encrypted_string > `"$APP_DIR\password.txt`"") # Read Encrypted password from txt file $sw1.writeline("`$my_secure_password_string = convertto-securestring (get-content `"$APP_DIR\password.txt`") -key (1..16)") # Create Credentail variable by using username and password $sw1.writeline("`$cred = New-Object System.Management.Automation.PSCredential `$Username,`$my_secure_password_string ") # Create Manage Account $sw1.writeline("New-SPManagedAccount -Credential `$cred") # Web site name $sw1.writeline("`$siteName = `"$SITENAME`"") # Web site running port $sw1.writeline("`$port = `"$SITEPORT`"") # Host header $sw1.writeline("`$hostHeader = `"`"") # Web site URL $sw1.writeline("`$url = `"http://$COMPUTERNAME`"") # Application pool name $sw1.writeline("`$appPoolName = `"$APPPOOLNAME`"") # Managed Account name $sw1.writeline("`$managedAccount = `"$COMPUTERNAME\$USERNAME`"") # DB server name $sw1.writeline("`$dbServer = `"$COMPUTERNAME\SharePoint`"") # DB name $sw1.writeline("`$dbName = `"WSS_Content_$SITEPORT`"") $sw1.writeline("`$authenticationMethod = `"NTLM`"") # Command let that will create sample app by using above declared variables $sw1.writeline("New-SPWebApplication -Name `$siteName -Port `$port -HostHeader `$hostHeader -URL `$url -ApplicationPool `$appPoolName -ApplicationPoolAccount (Get-SPManagedAccount `"`$managedAccount`") -DatabaseName `$dbName -DatabaseServer `$dbServer -AllowAnonymousAccess: `$allowAnonymous -AuthenticationMethod `$authenticationMethod") $sw1.close()
##################### EXECUTE THE Above created SCRIPT file AS AN ADMINISTRATOR#############################
Start-Process powershell -verb runas -ArgumentList "-file $WEBAPP_FILE"
############ CREATING A POWERSHELL SCRIPT FILE TO CREATE A SAMPLE POOL and the sample app into it################ New-Item $SITECOLLECTION_FILE -type file $sw = new-object system.IO.StreamWriter($SITECOLLECTION_FILE) # Add the below command let in to the starting of the script $sw.writeline("add-PSSnapin Microsoft.Sharepoint.powershell") # URL of site that we want to add in to pool $sw.writeline("`$siteURL = `"http://$COMPUTERNAME`:$SITEPORT/sites/test`"") $sw.writeline("`$owner = `"$COMPUTERNAME\$USERNAME`"") $sw.writeline("`$secondOwner = `"$COMPUTERNAME\$USERNAME`"") # type of template (we can also use some othter template) $sw.writeline("`$template = `"STS#0`"") $sw.writeline("`$description = `"`" ") # Command let that will add website in to pool. $sw.writeline("New-SPSite `$siteURL -OwnerAlias `$owner -SecondaryOwnerAlias `$secondOwner -name `"PowerShell for SharePoint`" -Template `$template -Description `$description") $sw.close() ##################### EXECUTE THE SCRIPT AS AN ADMINISTRATOR############################# Start-Process powershell -verb runas -ArgumentList "-file $SITECOLLECTION_FILE"

supported-os-details.exec-script:
$error.clear()
# Start of Json Parser
$script:tokens=New-Object System.Collections.Queue
$script:token=""
$script:pathValueMap=@{}  #HashTable between all keypath and mapping string value
$script:pathArray=@()     #HashTable is unsorted, so we need path Array which save sorted keys.

function addMatchInfoIntoToken($matches) {
    foreach ($match in $matches.matches) {
        $matchValue = $match.value
        if ( !($matchValue -match "^$SPACE$") ) { $script:tokens.Enqueue($matchValue) }
    }
}

function readToken() {
    $script:token=$script:tokens.Dequeue()
}

function tokenize($jsonString) {
    $ESCAPE = '(\\[^u]|\\u[0-9a-fA-F]{4})'
    $CHAR = '[^"\\]'
    $STRING = "`"$CHAR*($ESCAPE$CHAR*)*`""
    $NUMBER = '-?(0|[1-9][0-9]*)([.][0-9]*)?([eE][+-]?[0-9]*)?'
    $KEYWORD = 'null|false|true'
    $SPACE = '[ \t\r\n\v\f]+'
    $TOKENPATTERN = "$STRING|$NUMBER|$KEYWORD|$SPACE|."

    $script:tokens=New-Object System.Collections.Queue

    $m = ($jsonString | select-string -pattern $TOKENPATTERN -AllMatches)
    if ($m.length) {
        foreach ( $ml in $m ) {
            addMatchInfoIntoToken $ml
        }
    } else {
        addMatchInfoIntoToken $m
    }
}

function parse_array() {
    $index = 0
    $ary = ''
    readToken
    switch ($script:token) {
        "]" {}
        default {
            :lblWhile while ($true) {
                parse_value $args[0] "[$index]"
                $index = $index+1
                $ary = "$ary$script:value"
                readToken
                switch ($script:token) {
                    "]" { break lblWhile }
                    "," { $ary = "$ary,"; break }
                    default {
                        throw "EXPECTED , or ] GOT $script:token"
                    }
                }
                readToken
            }
        }
    }
    $script:value = "[$ary]"
}

function parse_object() {
    $obj = ''
    readToken
    switch ($script:token) {
        "}" { break;}
        default {
            :lblwhile while ($true) {
                if ($script:token -match "`".*`"") {
                    $key = $script:token;
                } else {
                    throw "EXPECTED string GOT $script:token"
                }
                readToken
                if ($script:token -eq ":") {
                    #DoNothing
                } else {
                    throw "EXPECTED : GOT $script:token"
                }
                readToken
                $subPath = $key.substring(1, $key.length-2) #For remove leading and tailing double quotation
                parse_value $args[0] "$subPath"
                $obj= "$obj$($key):$script:value"
                readToken
                switch ($script:token) {
                    "}" { break lblwhile }
                    "," {$obj = "$obj," ; break}
                    default {
                        throw "EXPECTED , or } GOT $script:token"
                    }
                }
                readToken
            }
        }
    }
    $script:value="{$obj}"
}

function parse_value() {
    if ($args[0]) {
        if ($args[1].StartsWith("["))  {
            $jpath = "$($args[0])$($args[1])"
        } else {
            $jpath = "$($args[0]).$($args[1])"
        }
    } else {
        $jpath = $args[1]
    }

    switch -regex ($script:token) {
        "^{$" { parse_object "$jpath" ; break }
        "^\[$" { parse_array "$jpath" ; break }
        # At this location, the only valid single character tokens are digitals.
        "^[^0-9]$" {
            throw "EXPECTED value GOT $script:token"
        }
        default {
            $script:value = $script:token
        }
    }
    if ( $script:value -eq '') { return }
    if ($jpath) {
        $script:pathValueMap.Add($jpath, $script:value)
        $script:pathArray+=$jpath
    } else {
        $script:pathValueMap.Add("", $script:value)
        $script:pathArray+=""
    }
}

function parse($jsonString) {
    $script:pathValueMap = @{}
    $script:pathArray=@()
    tokenize($jsonString)
    readToken
    parse_value
}

function getStringValueFromJson($jsonString, $keyPath) {
    parse $jsonString
    if ($script:pathValueMap.$keyPath) {
        return $script:pathValueMap.$keyPath
    } else {
        throw "keyPath $keyPath not found in JSON String"
    }
}

#
# This method will convert an JSON string to a powershell "object"
# A powershell "object" can be either a String or an array
#
# Note: this is a PRIVATE method, don't call it directly, unless you clear understand
# the impact to Json Parser private global variable $pathValueMap
#
function convertToScriptObject($stringValue, $keyPath) {
    if (!($stringValue.startsWith("["))) {
        return convertToScriptString($stringValue)
    } else {
        $arrayValue = @()
        foreach ($key in $script:pathArray) {
            if ($key.startsWith($keyPath)) {
                $subKey = $key.subString($keyPath.length)
                 if ($subKey -match  "^\[[0-9]+\]$" ) {
                     $arrayValue += convertToScriptString $script:pathValueMap.$key
                 }
            }
        }
        return ,$arrayValue
    }
}

#
#This method will convert an escaped Json char to a powershell char
#
function escapeJsonChar($jsonChar) {
    switch ($jsonChar) {
        '"' { return '"';};
        '/' { return "`/";};
        'b' { return "`b";};
        'f' { return "`f";};
        'r' { return "`r";};
        't' { return "`t";};
        'n' { return "`n";};
        '\' { return "`\";};
        default { return $jsonChar }
    }
}

#
# This method will convert an JSON string to PowershellString
# for example, automatically remove enclosing double quotation of JSON string
# 
# @getValueFromJson method will call this method automatically
# so in most case user needn't call this method explicitly
#
function convertToScriptString($stringValue) {
    switch -regex ($stringValue) {
        "^true$" {return $true}
        "^false$" {return $false}
        "^null$" {return $null}
        '^".*"$' {
            $stringValue = $stringValue.subString(1, $stringValue.length -2)
            $stringValue2 = ""
            $isEscaping = $False;
            foreach ($char in $stringValue.toCharArray()) {
                if ($isEscaping) {
                    # TODO We do not support \udddd format characer so far.
                    # To be support after we have I18n DBCS solution.
                    $isEscaping = $False;
                    $stringValue2 += escapeJsonChar($char)
                } else {
                    if ($char.Equals("\".toCharArray()[0])) {
                        $isEscaping = $True;
                        continue;
                    } else {
                        $isEscaping = $False;
                        $stringValue2 += $char
                    }
                }
            }
            return $stringValue2
        }
        default {return $stringValue}
    }
}

#
# This method will parse JSON String, return value of given JSON keypath.
# The returned value type will be:
#     String: whenever (1) the value of given JSON keypath is a JSON string (enclosed by double quotation)
#                   or (2) the value of given JSON keypath is a JSON object (enclosed by brace)
#     Number:  whenever the value of given JSON keypath is a number
#     Keyword: whenever the value of given JSON keypath is true/false/null
#     Array: whenever the value of given JSON keypath is a JSON array (enclosed by square)
#
# This method will automatically remove enclosing double quotation when return String or Array of String
#
# Example:
#     getValueFromJson '{"key1":"value1", "key2":{"subkey1":"subValue1"}}' 'key2.key2'
#     will return "subValue1"
# Example:
#     getValueFromJson '{"key1":["value1","value2"]}' 'key1[1]'
#     will return "value2"
# Example:
#     getValueFromJson '[{"key1":"value1"}, {"key1":"value2"}]' '[1].key1'
#     will return "value2"
# Example:
#     getValueFromJson '[{"key1":"value1"}, {"key1":"value2"}]' ''
#     will return an array with 2 elements.
#     first element is  '{"key1":"value1"}'
#     second element is '{"key1":"value2"}'
# Example:
#     getValueFromJson '{"key1":"value1"}' ''
#     $JSONVALUE will be same as input string.
#
function getValueFromJson($jsonString, $keyPath) {
    $stringValue = getStringValueFromJson "$jsonString" "$keyPath"
    $value = convertToScriptObject "$stringValue" "$keyPath"
    return ,$value
}

#End of Json Parser

#
# Convert JsonString to Powershell PSObject
# each element of target PSObject is a String or Array of String
# this method is dedicated designed for enrivonment variable setup
# Maybe we should move this method into another PS file?
#
function convertToPSObject ($jsonString) {

    $envObject = New-Object PSObject
    $strValue = getValueFromJson($jsonString, "")
    foreach ($envName in $script:pathArray) {
        if (($envName -match "^.*[\.\[\]]+.*") -or ($envName -eq "")) {
            continue
        } else {
            $envValue = convertToScriptObject $script:pathValueMap.$envName $envName
            $envObject | add-member -MemberType NoteProperty $envname  $envValue
        }
    }
    return ,$envObject
}


$vmw_appd_json_file = Get-Content "$($Args[1])"
$vmw_appd_json_obj = convertToPSObject $vmw_appd_json_file
$vmw_appd_json_props = $vmw_appd_json_obj.PsObject.Properties

# Assigning obj to local variable
ForEach ($vmw_appd_obj_prop in $vmw_appd_json_props)
{
 $vmw_appd_obj_prop_name = $vmw_appd_obj_prop.Name;
 #$vmw_appd_obj_prop_name_full = '$vmw_appd_json_obj' + '.' + $vmw_appd_obj_prop_name
 #$vmw_appd_obj_prop_value = Invoke-Expression $vmw_appd_obj_prop_name_full
 Set-Variable -Name $vmw_appd_obj_prop_name -value $vmw_appd_json_obj."$vmw_appd_obj_prop_name"
}


# Execute user's Powershell script
$error.clear()
$global:LASTEXITCODE = $null
. "$($Args[0])" 2>&1 | out-file -encoding Default "$($Args[3])"

if ($LASTEXITCODE -and $LASTEXITCODE -ne 0) {
 "`nABORT. LASTEXITCODE is non-zero.`n" | out-file -encoding Default -append $($Args[3])
 exit $LASTEXITCODE
}

if ($error.count -gt 0) {
 "`nABORT. Encountered error in Powershell.`n" | out-file -encoding Default -append $($Args[3])
 exit $error.count
 }

# Export Powershell variables to output properties file
ForEach ($vmw_appd_obj_prop in $vmw_appd_json_props)
{
 $vmw_appd_obj_prop_name = $vmw_appd_obj_prop.Name;
 $vmw_appd_obj_prop_type = (($vmw_appd_json_obj.($vmw_appd_obj_prop_name)).GetType()).Name

 # Export only String AppD's properties; Output Array properties is not currently supported.
 if ($vmw_appd_obj_prop_type.Contains("String")) {
      $vmw_appd_obj_prop_name_expanded = '$' + $vmw_appd_obj_prop_name

      # The ExpandString takes care of three cases. 
      # Case-I when the string is set via input properties
      # Case-II when the string is set via input properties and overridden in script (install/configure..)
      # Case-III lastly when it is only set via script (install/configure..) and left empty in input props
      #
      # Also note that powershell as is does not care if this variable was a computed 
      # or a string variable that filter happens at a different layer.
      #
      $expandedUserVariable = $ExecutionContext.InvokeCommand.ExpandString($vmw_appd_obj_prop_name_expanded)

     ($vmw_appd_obj_prop_name + '=' + $expandedUserVariable) | Add-Content $($Args[2]) 
 }
}

$error.clear()
