print ("getting the IP address for the nics...");
ip="";
nwIpMap = new TreeMap();
ne = NetworkInterface.getNetworkInterfaces();
while (ne.hasMoreElements()) {
    NetworkInterface netFace = (NetworkInterface)ne.nextElement();
    if(!netFace.isLoopback()) {
        Enumeration ae = netFace.getInetAddresses();
        while (ae.hasMoreElements()) {
            InetAddress address = (InetAddress) ae.nextElement();
            StringBuilder macAddress = new StringBuilder();
            if(address instanceof Inet4Address) {
                StringBuilder macAddress = new StringBuilder("");
                if(netFace.getHardwareAddress() != null) {
                    for (int i = 0; i < netFace.getHardwareAddress().length; i++) {
                        Object[] arr = new Object[]{netFace.getHardwareAddress()[i], (i < netFace.getHardwareAddress().length - 1) ? ":" : ""};
                        macAddress.append(String.format("%02X%s", arr));
                    }
                } else {
                    //if there is no MAC address, move to the next interface
                    continue;
                }
                if(StringUtils.isBlank(ip)) {
                    ip=address.getHostAddress();
                }
                nwIpMap.put(macAddress.toString().toUpperCase(), address.getHostAddress());
            }
        }
    }
}
totalBlueprintNics = nic_name_array.length;
totalVMNics = nwIpMap.size();
print ("Network interfaces/IPs found on the VM: " + nwIpMap.toString());
if(totalVMNics < totalBlueprintNics) {
    throw new Exception("Insufficient network interfaces, expected " + totalBlueprintNics + " but got " + totalVMNics);
} else if(totalBlueprintNics != totalVMNics) {
    print("WARNING: Total number of NICs specified in blueprint (" + totalBlueprintNics + ") does not match the number of NICs found on VM (" + totalVMNics + ").");
}
for(index=0;index < totalBlueprintNics; index++) {
    eval("mac="+nic_name_array[index]+"_MAC");
    ipAddr = nwIpMap.get(mac.toUpperCase());
    if(StringUtils.isBlank(ipAddr)) {
        throw new Exception("Invalid MAC address in the input properties: " + nic_name_array[index]+"_MAC=" + mac);
    }
    eval(nic_name_array[index]+"_ip"+"=ipAddr");
}
print("evaluated all the ips for the NICs");

