    readToken
    switch ($script:token) {
        "}" { break;}
        default {
            :lblwhile while ($true) {
                if ($script:token -match "`".*`"") {
                    $key = $script:token;
                } else {
                    throw "EXPECTED string GOT $script:token"
                }
                readToken
                if ($script:token -eq ":") {
                    #DoNothing
